import React, { Component, useEffect } from 'react'
import {
  CButton,
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CRow, CDataTable, CForm, CFormGroup, CFormText, CLink, CInput, CLabel, CSelect,
} from '@coreui/react';

import MuiAlert from '@material-ui/lab/Alert';
import Snackbar from '@material-ui/core/Snackbar';
import Button from '@material-ui/core/Button';

import CIcon from '@coreui/icons-react'
import CategoryService from '../../API/CategoryService';
import CourseService from '../../API/CourseService';
import UploadService from '../../API/UploadService';

function Alert(props) {
  return <MuiAlert elevation={6} {...props} />;
}

export default function Courses() {
  const { useState } = React;
  const [currentPage, setCurrentPage] = useState(1)
  const [currentPagination, setCurrentPagination] = useState(5)
  const getLastVal = (currentPage) => {
    if (currentPage * currentPagination < categoryList.length)
      return currentPage * currentPagination;
    return categoryList.length;
  }
  const fields = [
    { key: 'description',label:'Title', _style: { width: '20%' } },
    {
      key: 'actions',
      label: '',
      _style: { width: '1%' },
      sorter: false,
      filter: false
    },
  ]

  const [value, setValue] = React.useState(0);
  const [categoryList, setCategoryList] = React.useState([]);
  const [courseList, setCourseList] = React.useState([]);
  const [levelList, setLevelList] = React.useState([]);
  const [courseTitle, setCourseTitle] = React.useState('');
  const [courseDescription, setCourseDescription] = React.useState('');
  const [level, setLevel] = React.useState(0);
  const [category, setCategory] = React.useState(0);
  const [image, setImage] = React.useState(null);
  const [imageHeight, setImageHeight] = React.useState('0px');
  const [toast, showToast] = React.useState(false);
  const [toastMessage, setToastMessage] = React.useState('');
  const [toastClass, setToastClass] = React.useState('success');
  
  const [fileLoading, setFileLoading] = React.useState(false);
  const [loading, setLoading] = React.useState(false);
  const [imageFile, setImageFile] = React.useState('');

  React.useEffect(() => {
    getCategoryList();
    setLevelList([{levelId:1, name:'Beginner'},{levelId:2, name:'Intermediate'},{levelId:3, name:'Expert'}]);
  }, []);

  const getCategoryList = async () => {
    setLoading(true)
    // get categories from backend service via service api
    await CategoryService.getCategoryList('', (res) => {      
      console.log(res)     
      setCategoryList(res); 
      setLoading(false)
      getCourseList();            
    }, (err) => {
      setLoading(false)
    });
  };
  
  const getCourseList = async () => {
    setLoading(true)
    // get categories from backend service via service api
    await CourseService.getCourseList('', (res) => {      
      console.log(res)     
      setCourseList(res); 
      setLoading(false)
    }, (err) => {
      setLoading(false)
    });
  };

  function deleteCourse(item) {
    setLoading(true)
    CourseService.deleteCourse(item.courseId, (res) => {
      setToastClass('success');
      setToastMessage('Success: ' + res);
      showToast(true);
      setLoading(false)
    }, (err) => {
      setLoading(false)
    });  
  }

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  function fileUpload(file) {
    const formData = new FormData();
    formData.append('file', file);
    var re = /(?:\.([^.]+))?$/;
    var ext = re.exec(file.name)[1];

    UploadService.uploadFile(ext, formData, (res) => {
      console.log('data===>'+JSON.stringify(res));
      if(res) {
        setImageFile(res.filename);
        setImageHeight("125px");
      }
      setFileLoading(false);
    }, (err) => {
      setFileLoading(false);
    });
  }

  function setThumbnail(event) {
    setFileLoading(true);
    setImage(URL.createObjectURL(event.target.files[0]));
    fileUpload(event.target.files[0]);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setLoading(true)
    const formData = new FormData();
    formData.append('file', image);   
    CourseService.createCourse(
      {
        title: courseTitle,
        description: courseDescription,
        category: category,
        level: level,
        imageFile: imageFile
      }, (res) => {
        setToastClass('success');
        setToastMessage('Success: ' + res);
        showToast(true);
        setCourseTitle('');
        setCourseDescription('');
        setCategory(0);
        setImage(null);
        setImageHeight('0px');
        setImageFile('');
        setLoading(false)
      }, (err) => {
        setLoading(false)
      });
  }

  return (
    <div className="animated fadeIn">
      <CRow>
        <CCol xl="5">
          <CCard custom>
            <CCardHeader >
              Courses
            </CCardHeader>
            <CCardBody>
              <CDataTable
                items={courseList}
                fields={fields}
                // columnFilter
                tableFilter
                // footer
                itemsPerPageSelect
                itemsPerPage={currentPagination}
                dark={false}
                hover={false}
                border={true}
                outlined={true}
                loading={false}
                sorter
                // clickableRows
                pagination={{ doubleArrows: true, align: 'center' }}
                loading={loading}
                // onRowClick={(item,index,col,e) => {console.log(item,index,col,e)}}
                onPageChange={(val) => {
                  console.log('new page:', val);
                  setCurrentPage(val)
                }
                }
                // onPagesChange={(val) => console.log('new pages:', val)}
                onPaginationChange={(val) => {
                  console.log('new pagination:', val);
                  setCurrentPagination(val);
                }}
                // onFilteredItemsChange={(val) => console.log('new filtered items:', val)}
                // onSorterValueChange={(val) => console.log('new sorter value:', val)}
                // onTableFilterChange={(val) => console.log('new table filter:', val)}
                // onColumnFilterChange={(val) => console.log('new column filter:', val)}
                scopedSlots={{
                  'parentName':
                  (item) => (
                    <td>
                      { item.parent !== 0 &&
                          categoryList.find(pCat => pCat.categoryId === item.parent).name
                      }                      
                    </td>
                  ),
                  'actions':
                    item => {
                      return (
                        <td className="py-2">
                          <CButton color="light"  onClick={(e) => { e.preventDefault(); deleteCourse(item) }}><CIcon name="cil-trash" /></CButton>
                        </td>
                      )
                    }
                }}
              />
              <div style={{ textAlign: 'right' }}>{
                ((currentPage - 1) * currentPagination + 1) + '-' + getLastVal(currentPage) + ' of ' + categoryList.length
              }
              </div>
            </CCardBody>
          </CCard>
        </CCol>
        <CCol xl="7">
          <CCard custom>
            <CCardHeader id="headingOne" style={{ display: 'inline-flex' }}>
              {
                'Add Course'
              }
            </CCardHeader>
            <CCardBody>
              <CForm onSubmit={handleSubmit} className="form-horizontal" name='simpleForm'>
                {/* onSubmit={handleSubmit} */}
                <CFormGroup row>
                  <CCol md="4">
                    <CLabel htmlFor="coursetitle-input">Course Title</CLabel>
                  </CCol>
                  <CCol xs="12" md="8">
                    <CInput type="text"
                      label="Course Title"
                      name="coursetitle-input"
                      id="coursetitle-input"
                      placeholder="Please input course title"
                      autoComplete="given-name"
                      onChange={e => setCourseTitle(e.target.value)}
                      value={courseTitle}
                      required />
                  </CCol>
                </CFormGroup>
                <CFormGroup row>
                  <CCol md="4">
                    <CLabel htmlFor="categoryname-input">Course Description</CLabel>
                  </CCol>
                  <CCol xs="12" md="8">
                    <CInput type="text"
                      label="Course Description"
                      name="coursedescription-input"
                      id="coursedescription-input"
                      placeholder="Please input course description"
                      autoComplete="given-name"
                      onChange={e => setCourseDescription(e.target.value)}
                      value={courseDescription}
                      required />
                  </CCol>
                </CFormGroup>
                <CFormGroup row>
                  <CCol md="4">
                    <CLabel htmlFor="category">Category</CLabel>
                  </CCol>
                  <CCol xs="12" md="8">
                    <CSelect 
                      custom name="category" 
                      label="Category"
                      id="category"
                      onChange={e => setCategory(e.target.value)}
                      value={category}
                    >
                    {categoryList.map((category) => (
                      <option value={category.categoryId}>{category.name}</option>
                    ))}
                    </CSelect>
                  </CCol>
                </CFormGroup>
                <CFormGroup row>
                  <CCol md="4">
                    <CLabel htmlFor="level">Level</CLabel>
                  </CCol>
                  <CCol xs="12" md="8">
                    <CSelect 
                      custom name="level" 
                      label="Level"
                      id="level"
                      onChange={e => setLevel(e.target.value)}
                      value={level}
                    >
                    <option value={1}>Beginner</option>
                    <option value={2}>Intermediate</option>
                    <option value={3}>Expert</option>
                    </CSelect>
                  </CCol>
                </CFormGroup>
                <CFormGroup row>
                  <CCol xs={12} sm={12} md={12} lg={6}>
                    <Button variant="contained" component="label">
                      <PhotoLibraryIcon /> &nbsp; Upload Image
                      <input type="file" accept="image/*" style={{display:"none"}} onChange={setThumbnail}/>
                    </Button>
                  </CCol>                  
                  <CCol xs={12} sm={12} md={12} lg={6}
                    component={Paper}
                    style={{backgroundImage:`url(${image})`,height:`${imageHeight}`,backgroundRepeat:'no-repeat',backgroundPosition: 'center',}}>
                    
                  </CCol>
                </CFormGroup>
                
                <hr />
                <CButton type="submit" variant="outline" className="mr-1" color="primary" block><CIcon name="cil-save" /> Save</CButton>
                {/* <CButton type="reset" variant="outline" className="mr-1" color="danger" onClick={handleReset} block><CIcon name="cil-ban" /> Reset</CButton> */}
              </CForm>
            </CCardBody>
          </CCard>
        </CCol>
        <CCol sm="12" lg="6">
          <Snackbar
            anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
            open={toast}
            autoHideDuration={3000}
            onClose={() => { showToast(false); getCategoryList(); }}>
            <Alert
              severity={toastClass}
              action={
                <CButton  onClick={(e) => { e.preventDefault(); showToast(false); getCategoryList();  }}><CIcon name="cil-x" /></CButton>
              }
            >
              {toastMessage}
            </Alert>
          </Snackbar>
        </CCol>
      </CRow>
    </div>
  )
}
