import React from "react";

import axios from 'axios';
import { Card, CardActions, CardContent, CardMedia, Grid, Typography, Container, Link,
         Paper, Box, Divider }  from '@material-ui/core';
import { NavLink as RouterLink, Route } from "react-router-dom";
import Navigation from '../../../Layout/Navigation';
import Copyright from '../../../Layout/Copyright';
import ReactPlayer from 'react-player';

import styles from "../../../assets/material/jss/material-dashboard-pro-react/views/pricingPageStyle.js";
import  { useStyles }  from '../../Layout/Theme/PageTheme';
const useStyles1 = makeStyles(styles);

export default function PricingPage() {
  const classes1 = useStyles1();
  const classes = useStyles();
  const [title, setTitle] = React.useState('');
  const [category, setCategory] = React.useState('');
  const [level, setLevel] = React.useState('');
  const [description, setDescription] = React.useState('');
  const [imagePath, setImagePath] = React.useState('');
  const [videoPath, setVideoPath] = React.useState('');

  React.useEffect(() => {
    getCourseDetails();
  }, []);

  const getCourseDetails = async () => {
    const course = await axios({
      url: process.env.REACT_APP_AWS_API_ENDPOINT + '/course/details/' + props.match.params.id,
      method: 'get'
    }).then(response => response.data).then((data) => {
      return data;
    });
    if(course) {
      setTitle(course.title);
      setDescription(course.description);
      setLevel('Level: ' + JSON.parse(course.level).name);
      setCategory('Category: ' + JSON.parse(course.category).name);
      setImagePath(process.env.REACT_APP_AWS_BUCKET_CLOUDFRONT_URL + '/' + course.imageFile);
      setVideoPath(process.env.REACT_APP_AWS_BUCKET_CLOUDFRONT_URL + '/' + course.videoFile);
    }
  };

  return (
    <div className={classes1.container}>
      <Container className={classes.mainContent}>
        <Grid container direction="row" alignItems="flex-start" justify="center">
          <Grid item xs={12} sm={4}>
            <Box component={Paper} elevation={6}
              style={{backgroundImage:`url(${imagePath})`,
                    height:'125px',backgroundRepeat:'no-repeat',
                    backgroundPosition: 'center',backgroundSize: 'cover'}}>
            </Box>
            <Box py={2}>
              <Typography component="h3" gutterBottom>{category}</Typography>
              <Typography component="h3" gutterBottom>{level}</Typography>
              <Typography style={{textAlign:"justify"}}>{description}</Typography>
            </Box>
          </Grid>
          <Grid item xs={12} sm={8}>
            <Box px={3} pb={2}>
              <Typography component="h3" variant="h6" color="primary" gutterBottom>{title}</Typography>
              <Divider />
            </Box>
            <Box px={3}>
              <ReactPlayer url={videoPath} controls width="100%" height="100%" />
            </Box>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
}
