            <CardBody>
              <GridContainer justify="center">
                <GridItem xs={12} sm={12} md={12}>                 
                  <form id="signUpForm" className={classes.form} onSubmit={handleSubmit}>
                    <GridContainer>
                      <GridItem xs={12} sm={6}>
                        <CustomInput
                          formControlProps={{
                            fullWidth: true,
                            className: classes.customFormControlClasses
                          }}
                          inputProps={{
                            startAdornment: (
                              <InputAdornment
                                position="start"
                                className={classes.inputAdornment}
                              >
                                <Face className={classes.inputAdornmentIcon} />
                              </InputAdornment>
                            ),
                            onChange: event => {                      
                              setFirstname(event.target.value)
                            },
                            placeholder: "First Name..."
                          }}
                          value={firstname}
                        />
                      </GridItem>
                      <GridItem xs={12} sm={6}>
                          <CustomInput
                            formControlProps={{
                              fullWidth: true,
                              className: classes.customFormControlClasses
                            }}
                            inputProps={{
                              onChange: event => {                      
                                setLastname(event.target.value)
                              },
                              placeholder: "Last Name..."
                            }}
                            value={lastname}
                          />
                      </GridItem>
                    </GridContainer>
                    <CustomInput
                      formControlProps={{
                        fullWidth: true,
                        className: classes.customFormControlClasses
                      }}
                      inputProps={{
                        startAdornment: (
                          <InputAdornment
                            position="start"
                            className={classes.inputAdornment}
                          >
                            <Email className={classes.inputAdornmentIcon} />
                          </InputAdornment>
                        ),
                        onChange: event => {                      
                          setEmail(event.target.value)
                        },
                        type: "email",
                        placeholder: "Email..."
                      }}
                      value={email}
                    />
                    <CustomInput                      
                      formControlProps={{
                        fullWidth: true,
                        className: classes.customFormControlClasses
                      }}
                      inputProps={{
                        startAdornment: (
                          <InputAdornment
                            position="start"
                            className={classes.inputAdornment}
                          >
                            <LockOutlined className={classes.inputAdornmentIcon}>
                              lock_outline
                            </LockOutlined>
                          </InputAdornment>
                        ),
                        onChange: event => {                      
                          setPassword(event.target.value)
                        },
                        type: "password",
                        autoComplete: "off",
                        placeholder: "Password..."
                      }}
                      value={password}
                    />                                      
                    <div className={classes.center} style={{marginTop:30, marginBottom:0}}>
                      <Button type="submit" round color="primary">
                        Get started
                      </Button>
                    </div>
                  </form>
                </GridItem>
