import React , { Component } from 'react';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Box from '@material-ui/core/Box';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import MenuIcon from '@material-ui/icons/Menu';
import Button from '@material-ui/core/Button';
import { NavLink as RouterLink, Route } from "react-router-dom";
import List from '@material-ui/core/List';
import Drawer from '@material-ui/core/Drawer';
import Hidden from '@material-ui/core/Hidden';
import Divider from '@material-ui/core/Divider';
import Paper from '@material-ui/core/Paper';
import clsx from 'clsx';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';

import Navigation from '../../Layout/Navigation';
import { useStyles } from '../../Layout/Theme/DashboardTheme';
import Copyright from '../../Layout/Copyright';
import Devices from './Devices';
import DeviceDashboard from './DeviceDashboard';
import { deviceMenuItems } from './DeviceMenuItems';
import Logo from '../../../assets/images/logo.png';

export default function Modules() {
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);

  const drawer = (
    <div>
      {/* <Toolbar className={classes.toolbar}>               
          <img src={Logo} alt="Logo" style={{width: "70%", marginRight: "auto", marginLeft: "auto"}} />
      </Toolbar> */}
      <Divider />
      {/* <div className={classes.appBarSpacer}/> */}
      <List  >{deviceMenuItems}</List>
      <Divider />
    </div>
  );

  const container = window !== undefined ? () => window.document.body : undefined;
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);

  return (
    <div className={classes.root}>
      <Navigation />      
      <nav className={classes.drawer} aria-label="mailbox folders">
        <Hidden mdUp implementation="css">
          <Drawer
            container={container}
            variant="temporary"
            anchor={theme.direction === 'rtl' ? 'right' : 'left'}
            open={open}
            onClose={() => setOpen(!open)}
            classes={{paper: classes.drawerPaper,}}
            ModalProps={{keepMounted: true,}}>
            {drawer}
          </Drawer> 
        </Hidden>
        <Hidden smDown implementation="css">
          <Drawer classes={{ paper: classes.drawerPaper,}} variant="permanent" open>
            {drawer}
          </Drawer>
        </Hidden>
      </nav>

      <main className={classes.appBarShift} style={{marginLeft : 0}}>
        <div className={classes.appBarSpacer} />
        <Container maxWidth="lg" className={classes.container} >
          <Route path="/devicemanagement" exact><DeviceDashboard /></Route>
          <Route path="/devicemanagement/device"><Devices /></Route>
          <footer className={classes.footer} style={{marginTop: 10}}>
            <Copyright />
          </footer>

        </Container>

      </main>
      <style>{`
      .MuiDrawer-paper {
        top: 64px !important;
      }
      `}</style>
    </div>
  );
}
