import React, { Component } from 'react'
import {
  CButton,
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CCollapse,
  CRow,CListGroup, CListGroupItem, CBadge, CDataTable, CAlert,CFormGroup,CInputCheckbox,CLabel
} from '@coreui/react';

import usersData from './UsersData.js'

import { makeStyles } from '@material-ui/core/styles';
import clsx from 'clsx';
import IconButton from '@material-ui/core/IconButton';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

const useStyles = makeStyles((theme) => ({
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
}));


export default function DeviceDashboard() {
  const { useState } = React;
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(true);
  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  const [details, setDetails] = useState([])
  const toggleDetails = (index) => {
    const position = details.indexOf(index)
    let newDetails = details.slice()
    if (position !== -1) {
      newDetails.splice(position, 1)
    } else {
      newDetails = [...details, index]
    } 
    setDetails(newDetails)
  }

  const fields = [
    {
      key: 'check_device',
      label: '',
      _style: { width: '1%' },
      sorter: false,
      filter: false
    },
    { key: 'IMEI', _style: { width: '30%'} },        
    { key: 'DeviceType', _style: { width: '30%'} },
    { key: 'Status', _style: { width: '20%'} },
    { key: 'HeartBeats', _style: { width: '20%'} },
  ]

  const getBadge = (status)=>{
    switch (status) {
      case 'Online': return 'primary'
      case 'Offline': return 'secondary'
      default: return 'primary'
    }
  }

  return (
    <div className="animated fadeIn">
      <CRow> 
        <CCol xl="9">
          <CCard custom>
            <CCardHeader>
              Monitor
            </CCardHeader>
            <CCardBody>         
            <CDataTable
              items={usersData}
              fields={fields}
              // columnFilter
              tableFilter
              // footer
              itemsPerPageSelect={{align:'bottom'}}
              itemsPerPage={5}
              dark={false}
              hover={false}
              border={true}
              outlined={true}
              loading={false}
              sorter
              clickableRows={true}
              pagination={{ doubleArrows: true, align: 'center' }}
              // loading
              onRowClick={(item,index,col,e) => console.log(item,index,col,e)}
              // onPageChange={(val) => console.log('new page:', val)}
              onPagesChange={(val) => console.log('new pages:', val)}
              onPaginationChange={(val) => console.log('new pagination:', val)}
              // onFilteredItemsChange={(val) => console.log('new filtered items:', val)}
              // onSorterValueChange={(val) => console.log('new sorter value:', val)}
              // onTableFilterChange={(val) => console.log('new table filter:', val)}
              // onColumnFilterChange={(val) => console.log('new column filter:', val)}
              scopedSlots = {{
                'Status':
                  (item)=>(
                    <td>
                      <CBadge color={getBadge(item.Status)}>
                        {item.Status}
                      </CBadge>
                    </td>
                  ),
                'check_device':
                  item => {
                    return (
                      <td className="py-2">
                        <CFormGroup variant="checkbox" className="checkbox">
                          <CInputCheckbox id="checkbox{item.id}" name="checkbox3" value="option3" />
                        </CFormGroup>                      
                      </td>
                    )
                  }
              }}
            />
          </CCardBody>
          </CCard>
        </CCol>
        <CCol xl="3">
          <div id="accordion">
            <CCard custom>
              <CCardHeader id="headingOne" style={{alignSelf: 'center'}}>
                <div style={{display: 'inline-flex'}}>
                <CButton block color="link" className="text-left m-0 p-0" onClick={() => handleExpandClick()}>
                  <h5 className="m-0 p-0">Commands</h5>
                </CButton>
                <IconButton
                  className={clsx(classes.expand, {
                    [classes.expandOpen]: expanded,
                  })}
                  onClick={handleExpandClick}
                  aria-expanded={expanded}
                  aria-label="show more"
                  style={{width:24, height:24, maringLeft:0, padding:0}}>
                  <ExpandMoreIcon style={{maring:0, padding:0}}/>
                </IconButton> 
                </div>
              </CCardHeader>
              <CCollapse custom show={expanded}>
                  <CListGroup>
                    <CListGroupItem>Cras justo odio</CListGroupItem>
                    <CListGroupItem>Dapibus ac facilisis in</CListGroupItem>
                    <CListGroupItem>Morbi leo risus</CListGroupItem>
                    <CListGroupItem>Porta ac consectetur ac</CListGroupItem>
                    <CListGroupItem>Vestibulum at eros</CListGroupItem>
                  </CListGroup>
              </CCollapse>
            </CCard>                 
          </div>            
        </CCol>
      </CRow>
    </div>      
  )
}
