import React, { Component } from 'react'
import {
  CButton,
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CRow, CDataTable, CForm, CFormGroup,CFormText,CLink,CInput,CLabel,CSelect,
  CCardFooter,CInvalidFeedback
} from '@coreui/react';
import CIcon from '@coreui/icons-react'
import { Formik } from 'formik'
import * as Yup from 'yup'

import devicesData from './DevicesData.js'

const validationSchema = function (values) {
  return Yup.object().shape({
    imei: Yup.string()
    .min(2, `First name has to be at least 2 characters`)
    .required('IMEI is required'),  
  })
}

const validate = (getValidationSchema) => {
  return (values) => {
    const validationSchema = getValidationSchema(values)
    try {
      validationSchema.validateSync(values, { abortEarly: false })
      return {}
    } catch (error) {
      return getErrorsFromValidationError(error)
    }
  }
}

const getErrorsFromValidationError = (validationError) => {
  const FIRST_ERROR = 0
  return validationError.inner.reduce((errors, error) => {
    return {
      ...errors,
      [error.path]: error.errors[FIRST_ERROR],
    }
  }, {})
}

const initialValues = {
  imei: "",
}

const onSubmit = (values, { setSubmitting, setErrors }) => {
  setTimeout(() => {
    alert(JSON.stringify(values, null, 2))
    // console.log('User has been successfully saved!', values)
    setSubmitting(false)
  }, 2000)
}

const findFirstError = (formName, hasError) => {
  const form = document.forms[formName]
  for (let i = 0; i < form.length; i++) {
    if (hasError(form[i].name)) {
      form[i].focus()
      break
    }
  }
}

const validateForm = (errors) => {
  findFirstError('simpleForm', (fieldName) => {
    return Boolean(errors[fieldName])
  })
}


export default function Devices() {
  const { useState } = React;

  const [currentPage, setCurrentPage] = useState(1)
  const [currentPagination, setCurrentPagination] = useState(5)
  const getLastVal=(currentPage)=>{
    if(currentPage*currentPagination < devicesData.length)
      return currentPage*currentPagination;
    return devicesData.length;
  }
  const fields = [    
    { key: 'IMEI', _style: { width: '30%'} },        
    { key: 'DeviceType', _style: { width: '30%'} },
    {
      key: 'actions',
      label: '',
      _style: { width: '1%' },
      sorter: false,
      filter: false
    },
  ]
  
  const removeDevice=(item)=>{
    const device = devicesData.find( device => device.id === item.id)
    //console.log(item.id)
    //console.log(device)    
    var index = devicesData.indexOf(device)
    if (index !== -1) {
      devicesData.splice(index, 1);
    }
    console.log(devicesData);
  }

  return (    
    <div className="animated fadeIn">
      <CRow> 
        <CCol xl="7">
          <CCard custom>
            <CCardHeader >  
              <CFormGroup style={{padding:0, margin:0}}  row>            
                <CCol md="4" style={{alignSelf:'center'}}>
                  Device List
                </CCol>
                <CCol xs="12" md="8" style={{textAlign:'right'}}>
                  <CButton  variant="outline" size="sm" color="primary"  onClick={()=> document.getElementById('imei-input').value='' } ><CIcon name="cil-plus" />Add Device</CButton>
                </CCol>
              </CFormGroup>              
            </CCardHeader>
            <CCardBody>         
            <CDataTable
              items={devicesData}
              fields={fields}
              // columnFilter
              tableFilter
              // footer
              itemsPerPageSelect
              itemsPerPage={currentPagination}
              dark={false}
              hover={false}
              border={true}
              outlined={true}
              loading={false}
              sorter
              clickableRows
              pagination={{ doubleArrows: true, align: 'center' }}
              // loading
              onRowClick={(item,index,col,e) => 
                {
                console.log(item,index,col,e)
                document.getElementById('imei-input').value = item.IMEI
                }
              }
              onPageChange={(val) => {
                  console.log('new page:', val);
                  setCurrentPage(val)
                }
              }
              onPagesChange={(val) => 
                console.log('new pages:', val)
              }
              onPaginationChange={(val) => {
                console.log('new pagination:', val);
                setCurrentPagination(val);
              }}
              // onFilteredItemsChange={(val) => console.log('new filtered items:', val)}
              // onSorterValueChange={(val) => console.log('new sorter value:', val)}
              // onTableFilterChange={(val) => console.log('new table filter:', val)}
              // onColumnFilterChange={(val) => console.log('new column filter:', val)}
              scopedSlots = {{
                'IMEI':
                  (item)=>(
                    <td>
                        <CLink to={`/modules/devicemanagement/devices/${item.id}`}>{item.IMEI}</CLink>
                    </td>
                  ),
                'actions':
                  item => {
                    return (
                      <td className="py-2">
                        <CButton color="light" onClick={()=>removeDevice(item)}><CIcon name="cil-trash" /></CButton>                      
                      </td>
                    )
                  }
              }}
            />
            <div style={{textAlign:'right'}}>{              
              ((currentPage-1)*currentPagination+1) + '-' + getLastVal(currentPage) +' of '+devicesData.length
              } 
            </div>
          </CCardBody>
          </CCard>
        </CCol>
        <CCol xl="5">
            <CCard custom>
              <CCardHeader id="headingOne" style={{ display: 'inline-flex'}}> 
                Add Device                
              </CCardHeader>    
              <Formik
                    initialValues={initialValues}
                    validate={validate(validationSchema)}
                    onSubmit={onSubmit}
                  >
                  {
                    ({
                      values,
                      errors,
                      touched,
                      status,
                      dirty,
                      handleChange,
                      handleBlur,
                      handleSubmit,
                      isSubmitting,
                      isValid,
                      handleReset,
                      setTouched
                    }) => (                         
                    <CCardBody>                                       
                     <CForm onSubmit={handleSubmit} noValidate name='simpleForm'>
                      <CFormGroup row>
                        <CCol md="4">
                          <CLabel htmlFor="text-input">IMEI</CLabel>
                        </CCol>
                        <CCol xs="12" md="8">
                          {/* <CInput id="imei-input" name="text-input" placeholder="Please input imei" /> */}
                          <CInput type="text"
                              name="imei-input"
                              id="imei-input"
                              placeholder="Please input imei"
                              autoComplete="given-name"
                              valid={!errors.imei}
                              invalid={touched.imei && !!errors.imei}
                              autoFocus={true}
                              required
                              onChange={handleChange}
                              onBlur={handleBlur}
                              value={values.imei} />
                          <CInvalidFeedback>{errors.imei}</CInvalidFeedback>
                        </CCol>
                      </CFormGroup>
                      <CFormGroup row>
                        <CCol md="4">
                          <CLabel htmlFor="select">Device Type</CLabel>
                        </CCol>
                        <CCol xs="12" md="8">
                          <CSelect custom name="select" id="select">
                            <option value="0">Raspberry PI</option>
                          </CSelect>
                        </CCol>
                      </CFormGroup>                
                      <CCardFooter style={{padding:0, textAlign:'center'}}>
                        <CButton type="submit"  variant="outline" size="lg" color="primary" block><CIcon name="cil-save" /> {isSubmitting ? 'Wait...' : 'Save'}</CButton>
                        {/* <CButton type="reset" color="danger" className="mr-1" onClick={handleReset}>Reset</CButton> */}
                      </CCardFooter>                    
                    </CForm>        
                </CCardBody>                    
              )}
              </Formik>             
            </CCard>                 
        </CCol>
      </CRow>
    </div>      
  )
}
