export default function DeviceDashboard() {
  const { useState } = React;
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(true);
  const [details, setDetails] = React.useState([]);
  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  const toggleDetails=(index) => {
    const position = details.indexOf(index);
    (position !== -1) ? details.splice(position, 1) :
      details.push(index);
    setDetails(details);
  }

  const getBadge = (status)=>{
    switch (status) {
      case 'Active': return 'success'
      case 'Inactive': return 'secondary'
      case 'Pending': return 'warning'
      case 'Banned': return 'danger'
      default: return 'primary'
    }
  };

  return (
    <div className="animated fadeIn">
      <CRow> 
        <CCol xl="9">
          <CCard custom>
            <CCardHeader>
              Monitor
            </CCardHeader>
            <CCardBody>
            <CDataTable
              items={usersData}
              fields={fields}
              columnFilter
              tableFilter
              itemsPerPageSelect
              itemsPerPage={5}
              hover
              
              pagination
              scopedSlots = {{
                'status':
                  (item)=>(
                    <td>
                      <CBadge color={getBadge(item.status)}>
                        {item.status}
                      </CBadge>
                    </td>
                  ),
                'show_details':
                  (item, index)=>{
                    return (
                      <td className="py-2">
                        <CButton
                          color="primary"
                          variant="outline"
                          shape="square"
                          size="sm"
                          onClick={()=>{toggleDetails(index)}}
                        >
                          {details.includes(index) ? 'Hide' : 'Show'}
                        </CButton>
                      </td>
                      )
                  },
                'details':
                    (item, index)=>{
                      return (
                      <CCollapse custom show={details.includes(index)}>
                        <CCardBody>
                          <h4>
                            {item.username}
                          </h4>
                          <p className="text-muted">User since: {item.registered}</p>
                          <CButton size="sm" color="info">
                            User Settings
                          </CButton>
                          <CButton size="sm" color="danger" className="ml-1">
                            Delete
                          </CButton>
                        </CCardBody>
                      </CCollapse>
                    )
                  }
              }}
            >
              loading...
            </CDataTable>

          </CCardBody>
          </CCard>
        </CCol>
        <CCol xl="3">
          <div id="accordion">
            <CCard custom>
              <CCardHeader id="headingOne" style={{alignSelf: 'center'}}>
                <div style={{display: 'inline-flex'}}>
                <CButton block color="link" className="text-left m-0 p-0" onClick={() => handleExpandClick()}>
                  <h5 className="m-0 p-0">Commands</h5>
                </CButton>
                <IconButton
                  className={clsx(classes.expand, {
                    [classes.expandOpen]: expanded,
                  })}
                  onClick={handleExpandClick}
                  aria-expanded={expanded}
                  aria-label="show more"
                  style={{width:24, height:24, maringLeft:0, padding:0}}>
                  <ExpandMoreIcon style={{maring:0, padding:0}}/>
                </IconButton> 
                </div>
              </CCardHeader>
              <CCollapse custom show={expanded}>
                  <CListGroup>
                    <CListGroupItem>Cras justo odio</CListGroupItem>
                    <CListGroupItem>Dapibus ac facilisis in</CListGroupItem>
                    <CListGroupItem>Morbi leo risus</CListGroupItem>
                    <CListGroupItem>Porta ac consectetur ac</CListGroupItem>
                    <CListGroupItem>Vestibulum at eros</CListGroupItem>
                  </CListGroup>
              </CCollapse>
            </CCard>                 
          </div>            
        </CCol>
      </CRow>
    </div>      
  )
}
