import React from "react";
import {
  CButton,
  CCard,
  CCardBody,
  CCardFooter,
  CCol,
  CContainer,
  CForm,
  CInput,
  CInputGroup,
  CInputGroupPrepend,
  CInputGroupText,
  CRow
} from '@coreui/react'
import CIcon from '@coreui/icons-react'

import { NavLink as RouterLink, Route, useHistory } from "react-router-dom";
import Amplify, { Auth } from 'aws-amplify';

// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";

import styles from "../../../assets/material/jss/material-dashboard-pro-react/views/registerPageStyle";

const useStyles = makeStyles(styles);

export default function RegisterPage() {
  const classes = useStyles();
  const history = useHistory();
  const [firstname, setFirstname] = React.useState('');
  const [lastname, setLastname] = React.useState('');
  const [email, setEmail] = React.useState('');
  const [password, setPassword] = React.useState('');

  Amplify.configure({
    Auth: {
      region: process.env.REACT_APP_AWS_REGION,
      userPoolId: process.env.REACT_APP_AWS_USER_POOL_ID,
      userPoolWebClientId: process.env.REACT_APP_AWS_USER_POOL_CLIENT_ID
    }
  });

  async function handleSubmit(event) {
    event.preventDefault();
    try {
      const { userConfirmed } = await Auth.signUp({
        username: email,
        password: password,
        attributes: {
          name: firstname + ' ' + lastname
        }
      });
      if(!userConfirmed) {
        localStorage.setItem('userEmail', email);
        history.push('/auth/verify');
      }
      //console.log(user);
      setFirstname('');
      setLastname('');
      setEmail('');
      setPassword('');
    } catch (error) {
      console.log('error signing up:', error);
    }
  }

  return (
    <div className={classes.container}>
      <CContainer>
        <CRow className="justify-content-center">
          <CCol md="9" lg="7" xl="6">
            <CCard className="mx-4">
              <CCardBody className="p-4">
                <CForm id="signUpForm" onSubmit={handleSubmit}>
                  <h1 style={{color:"#3c4b64"}}>Contact Us</h1>
                  <p className="text-muted"> Drop a message, and we'll get back to you soon.</p>
                  <CRow className="justify-content-center">
                    <CCol md="6" lg="6" xl="6">
                      <CInputGroup className="mb-3">
                        <CInputGroupPrepend>
                          <CInputGroupText>
                            <CIcon name="cil-user" />
                          </CInputGroupText>
                        </CInputGroupPrepend>
                        <CInput 
                          type="text" 
                          placeholder="First Name" 
                          autoComplete="firstname" 
                          onChange={e => setFirstname(e.target.value)}
                          required
                          value={firstname}
                          />
                      </CInputGroup>
                    </CCol>
                    <CCol md="6" lg="6" xl="6">
                      <CInputGroup className="mb-3">
                        <CInput 
                          type="text" 
                          placeholder="Last Name" 
                          autoComplete="lastname" 
                          onChange={e => setLastname(e.target.value)}
                          required
                          value={lastname}
                        />
                      </CInputGroup>
                    </CCol>
                  </CRow>
                  <CInputGroup className="mb-3">
                    <CInputGroupPrepend>
                      <CInputGroupText><CIcon name="cil-envelope-closed" /></CInputGroupText>
                    </CInputGroupPrepend>
                    <CInput 
                      type="email" 
                      placeholder="Email" 
                      autoComplete="email" 
                      onChange={e => setEmail(e.target.value)}
                      required
                      value={email}
                    />
                  </CInputGroup>
                  <CInputGroup className="mb-3">
                    <CInputGroupPrepend>
                      <CInputGroupText>
                        <CIcon name="cil-lock-locked" />
                      </CInputGroupText>
                    </CInputGroupPrepend>
                    <CInput 
                      type="password" 
                      placeholder="Password" 
                      autoComplete="new-password" 
                      onChange={e => setPassword(e.target.value)}
                      required
                      value={password}
                    />
                  </CInputGroup>
                  <CButton type="submit"  color="success" block>Create Account</CButton>
                </CForm>
              </CCardBody>              
            </CCard>
          </CCol>
        </CRow>
      </CContainer>     
    </div>
  );
}
