import React from 'react';
import Link from '@material-ui/core/Link';
import { makeStyles } from '@material-ui/core/styles';
import axios from 'axios';
import Button from '@material-ui/core/Button';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Box from '@material-ui/core/Box';
import Switch from '@material-ui/core/Switch';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import TextField from '@material-ui/core/TextField';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import InputLabel from '@material-ui/core/InputLabel';
import Grid from '@material-ui/core/Grid';
import AddPhotoAlternateIcon from "@material-ui/icons/AddPhotoAlternate";

function createData(id, title, category, level, duration, price, subscriptions, status) {
  return { id, title, category, level, duration, price, subscriptions, status };
}

function TabPanel(props) {
  const { children, value, index } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      // id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
    >
      {value === index && (
        <Box p={4}>
          {children}
        </Box>
      )}
    </div>
  );
}

const rows = [
  createData(0, 'AWS Fundamentals', 'Information Technology', 'Beginner', 120, 200, 14, 'Active'),
  createData(1, 'Brand Management', 'Marketting', 'Expert', 60, 300, 7, 'Active'),
  createData(2, 'Weight Management', 'Nutrition', 'Beginner', 90, 240, 11, 'InActive'),
  createData(3, 'The Unix Workbench', 'Computer Science', 'Intermediate', 45, 150, 3, 'Active'),
  createData(4, 'Quantitative Modeling', 'Finance', 'Beginner', 75, 340, 19, 'InActive'),
  createData(5, 'Data Visualization', 'Data Science', 'Expert', 120, 450, 20, 'Active'),
];

function preventDefault(event) {
  event.preventDefault();
}

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    flexGrow: 1,
  },
  dataTable: {
    marginTop: theme.spacing(3),
  },
  button: {
    margin: theme.spacing(1),
  }
}));

export default function Courses() {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);
  const [categoryList, setCategoryList] = React.useState([]);
  const[category, setCategory] = React.useState(0);
  const[level, setLevel] = React.useState(0);
  const[status, setStatus] = React.useState(false);

  React.useEffect(() => {
    getCategoryList();
  }, []);

  const getCategoryList = async () => {
    const dataList = await axios({
      url: 'https://hfa3ovai45.execute-api.us-east-2.amazonaws.com/api/category',
      method: 'get'
    }).then(response => response.data).then((data) => {
      return data;
    });
    if(dataList) { setCategoryList(dataList); }
  };

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  const handleStatusChange = (event) => {
    setStatus(event.target.checked);
  };

  const onImageChange = () => {

  }

  const onImageUpload = () => {
    
  }

  return (
    <React.Fragment>
      <div className={classes.root}>
        {/* <Grid container spacing={2}> */}
          {/* <Grid container xs={12}> */}
            <Paper>
              <Tabs indicatorColor="primary" textColor="primary" value={value} onChange={handleChange} variant="fullWidth">
                <Tab label="Courses" />
                <Tab label="Add Course" />
              </Tabs>
              <TabPanel value={value} index={0}>
                {/* <Grid container xs={12}> */}
                  <Table size="small">
                    <TableHead>
                      <TableRow>
                        <TableCell>Title</TableCell>
                        <TableCell>Category</TableCell>
                        <TableCell>Level</TableCell>
                        <TableCell>Status</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {rows.map((row) => (
                        <TableRow key={row.id}>
                          <TableCell>{row.title}</TableCell>
                          <TableCell>{row.category}</TableCell>
                          <TableCell>{row.level}</TableCell>
                          <TableCell>{row.status}</TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                {/* </Grid>                                 */}
              </TabPanel>
              <TabPanel value={value} index={1}>
                <Grid container xs={12}>
                  <form>
                    <TextField required id="Course Title" size="small" margin="normal" label="Course Title" variant="outlined" fullWidth />
                    <TextField required id="Course Description" size="small" margin="normal" label="Course Description" variant="outlined" fullWidth />
                    <FormControl required fullWidth size="small" margin="normal" variant="outlined" className={classes.formControl} >
                      <InputLabel id="categoryLabel">Category</InputLabel>
                      <Select
                        labelId="categoryLabel"
                        id="category"
                        label="Parent Category"
                        onChange={e => setCategory(e.target.value)}
                        value={category}
                      >
                        <MenuItem value={0}>None</MenuItem>
                        {categoryList.map((category) => (
                          <MenuItem value={category.categoryId}>{category.name}</MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                    <FormControl required fullWidth size="small" margin="normal" variant="outlined" className={classes.formControl} >
                      <InputLabel id="levelLabel">Level</InputLabel>
                      <Select
                        labelId="levelLabel"
                        id="level"
                        label="level"
                        onChange={e => setLevel(e.target.value)}
                        value={level}                        
                      >
                        <MenuItem value={0}>Beginner</MenuItem>
                        <MenuItem value={1}>Intermediate</MenuItem>
                        <MenuItem value={2}>Expert</MenuItem>
                      </Select>
                    </FormControl>
                    <FormControl margin="normal">
                      <div>
                        <FormControlLabel
                          control={<Switch size="small" checked={status} onChange={handleStatusChange} name="checked" />}
                          label="Status"
                        />
                        <input
                          style={{ display: "none" }}
                          accept="image/*"
                          id="image-file"
                          multiple
                          type="file"
                          onChange={onImageChange}
                        />
                        <label htmlFor="image-file">
                          <Button className={classes.button} onClick={onImageUpload} color="default" variant="contained" component="span">
                            Upload Image
                          </Button>
                        </label>
                      
                        <input
                          style={{ display: "none" }}
                          accept="video/*"
                          id="video-file"
                          multiple
                          type="file"
                        />
                        <label htmlFor="video-file">
                          <Button className={classes.button} color="default" variant="contained" component="span">
                            Upload Video
                          </Button>
                        </label>
                      </div>
                    </FormControl>                      
                  </form> 
                  <Button fullWidth type="submit" variant="contained" color="primary">
                    Submit
                  </Button> 
                </Grid>
              </TabPanel>
            </Paper>
          {/* </Grid> */}
        {/* </Grid> */}
      </div>
    </React.Fragment>
  );
}
