import React from "react";

import {
  CButton,
  CCard,
  CCardBody,
  CCardFooter,
  CCol,
  CContainer,
  CForm,
  CInput,
  CInputGroup,
  CInputGroupPrepend,
  CInputGroupText,
  CRow,
  CTextarea,
  CFormGroup
} from '@coreui/react'
import CIcon from '@coreui/icons-react'

import axios from 'axios';

export default function RefundPage() {
  const [name, setName] = React.useState('');
  function handleSubmit(event) {
    event.preventDefault();
    axios({
      url: 'https://hfa3ovai45.execute-api.us-east-2.amazonaws.com/api/category',
      method: 'post',
      data: {
        'name': name
      },
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(response => response.data).then((data) => {
      //setToastClass('success');
      //setToastMessage('Success: ' + data);
      //showToast(true);
      //setCategoryName('');
      //setParent(0);
    });
  }

  return (
    <div className="animated fadeIn">
      <CContainer>
        <CRow className="justify-content-center">
          <CCol md="9" lg="7" xl="6">
            <CCard className="mx-4">
              <CCardBody className="p-4">
                <CForm id="signUpForm" onSubmit={handleSubmit}>
                  <h1 style={{color:"#3c4b64"}}>Contact Us</h1>
                  <p className="text-muted">Drop a message, and we'll get back to you soon.</p>
                  <CInputGroup className="mb-3">
                    <CInputGroupPrepend>
                      <CInputGroupText>
                        <CIcon name="cil-user" />
                      </CInputGroupText>
                    </CInputGroupPrepend>
                    <CInput 
                      type="text" 
                      placeholder="Name" 
                      autoComplete="name" 
                      onChange={e => setName(e.target.value)}
                      required
                      value={name}
                      />
                  </CInputGroup>
                  <CInputGroup className="mb-3">
                    <CInputGroupPrepend>
                      <CInputGroupText><CIcon name="cil-envelope-closed" /></CInputGroupText>
                    </CInputGroupPrepend>
                    <CInput 
                      type="email" 
                      placeholder="Email" 
                      autoComplete="email" 
                      onChange={e => setName(e.target.value)}
                      required
                      value={name}
                    />                    
                  </CInputGroup>

                  <CInputGroup className="mb-3">
                    <CInputGroupPrepend>
                      <CInputGroupText>@</CInputGroupText>
                    </CInputGroupPrepend>
                    <CInput 
                      type="text" 
                      placeholder="Subject" 
                      autoComplete="subject" 
                      onChange={e => setName(e.target.value)}
                      required
                      value={name}
                    />                    
                  </CInputGroup>
                  <CTextarea 
                      name="message" 
                      id="message" 
                      rows="5"
                      placeholder="Message" 
                      onChange={e => setName(e.target.value)}
                      required
                      value={name}
                      style={{marginBottom:10}}
                    />

                  <CButton type="submit"  color="success" block>Refund</CButton>
                </CForm>
              </CCardBody>              
            </CCard>
          </CCol>
        </CRow>
      </CContainer>       
    </div>
  );
}
