import React from "react";

import {
  CBadge,
  CCard,
  CCardBody,
  CCardFooter,
  CCardHeader,
  CCol,
  CRow,
  CContainer,
  CButton
} from  '@coreui/react'

import CIcon from '@coreui/icons-react'

import StripeCheckout from 'react-stripe-checkout';

// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
import axios from 'axios';

// import  { useStyles }  from '../../Layout/Theme/PageTheme';
import styles from "../../../assets/material/jss/material-dashboard-pro-react/views/pricingPageStyle.js";

const useStyles = makeStyles(styles);
export default function HomePage() {

  const classes = useStyles();
  const [userid, setUserid] = React.useState('userid_111')
  const [course1, setCourse1] = React.useState({name:'Introduction to IOT',description:'8 Classes', amount:80, currency:'SGD', isPurchased:false})
  const [course2, setCourse2] = React.useState({name:'IOT Intermediate Level',description:'48 Classes', amount:100, currency:'SGD', isPurchased:false})
  const [stripeKey, setStripeKey] = React.useState('pk_test_51HoJ7lEW8ELLg74EpUwGfDBHRE818ndk53WS4MP1OfHKGcUhppaQjdrxZJ0SHQ33hxJAi7sPNIJkhqoPJF6940Fr00txoaVRxn')

  const [courseList, setCourseList] = React.useState([]);

  React.useEffect(() => {
    getCourseList();
  }, []);

  const getCourseList = async () => {
    const dataList = await axios({
      url: process.env.REACT_APP_AWS_API_ENDPOINT + '/course',
      method: 'get'
    }).then(response => response.data).then((data) => {
      return data;
    });
    if(dataList) { setCourseList(dataList); }
  };

  function addPayment(amount, currency, cousreid, token) {       
    console.log('token:'+token)
    axios({
      url: process.env.REACT_APP_AWS_API_ENDPOINT + '/payment',
      method: 'post',
      data: {
        'stripeToken': token,
        'currency': currency,
        'userid': userid,
        'courseid': cousreid,
        'amount': JSON.stringify(amount),
        'paymenttime': Date.now().toString()
      },
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(response => response.data).then((data) => {
      console.log(data)
    }).catch(error => { 
      if (error.response) {
        console.log(error.response.data);
        console.log(error.response.status);
        console.log(error.response.headers);
      } else if (error.request) {
          console.log(error.request);
      } else {
          console.log('Error', error.message);
      }
      console.log(error.config);
    })     
  }

  function onToken1(token) 
  {
    addPayment(course1.amount*100, course1.currency, 'courseid_111',JSON.stringify(token))
  }

  function onToken2(token) 
  {
    addPayment(course2.amount*100, course2.currency, 'courseid_222', JSON.stringify(token))
  }

  function onOpened()
  {
    // <style>{`
    //   .TestModeInfo-LegacyWarning {
    //     visibility: hidden;
    //   }
    //   .brandingLogoView {
    //     visibility: hidden;
    //   }
    // `}
    // </style>
    console.log("opened")
  }

  return (
    <div className={classes.container}>
      <CContainer style={{marginBottom:20}}>
        <CRow className="justify-content-center">
          <h4 >Get a chance to win lifetime education for Free</h4>
        </CRow>
        <CRow className="justify-content-center">
          <h4 >No lock-ins. Cancel anytime! We're mission - driven, not monday - driven.</h4>
        </CRow>
      </CContainer>
       {/* <GridContainer justify="center">
        <GridItem xs={12} sm={12} md={12}>
          <h2 className={classes.title}>Get a chance to win lifetime education for Free</h2>
          <h5 className={classes.description}>
            No lock-ins. Cancel anytime! We're mission - driven, not monday - driven.
          </h5>
        </GridItem>
      </GridContainer> */}
      <CContainer>
        <CRow className="justify-content-center">
          <CCol xs="12" sm="12" md="4" >
            <CCard style={{borderRadius:'1rem', marginLeft:10,marginRight:10}}>
              <CCardHeader style={{borderTopLeftRadius:"1rem",borderTopRightRadius:"1rem", background:"#fff8f5"}}>   
                <CRow className="justify-content-center" style={{textAlign:'center', marginTop:20}}>    
                  <span className="h5" style={{color:"#000000"}}>Introduction to IOT</span>   
                </CRow>
                <CRow className="justify-content-center" style={{textAlign:'center'}}>
                  <span className="h6" style={{color:"#000000"}}>8 Classes</span>
                </CRow>
              </CCardHeader>
              <CCardBody style={{paddingLeft:'3px', paddingRight:'3px'}}>
                <CContainer>
                  <CRow className="justify-content-center" style={{textAlign:'center',marginBottom:20}}>
                    <table>
                      <tr>
                        <td><span className="h6" style={{color:"#000000"}}>Curriculum</span></td>
                      </tr>
                      <tr>
                        <td><span  style={{color:"#000000",fontSize:'3mm'}}>Sequences, Algorithms, variables, Functions, Events</span></td>
                      </tr>
                    </table>
                  </CRow>
                </CContainer>
                <CContainer >
                  <CRow className="justify-content-center"  style={{textAlign:'center',marginBottom:20}}>
                    <table>
                      <tr>
                        <td><span className="h6" style={{color:"#000000"}}>Activities</span></td>
                      </tr>
                      <tr>
                        <td><span  style={{color:"#000000", fontSize:'3mm'}}>Create your own games and basic apps</span></td>
                      </tr>
                    </table>
                  </CRow>
                </CContainer>
                <CContainer>
                  <CRow className="justify-content-center" style={{textAlign:'center',marginBottom:20}}>
                    <table>
                      <tr>
                        <td><span className="h6" style={{color:"#000000"}}>Cognitive Benefits</span></td>
                      </tr>
                      <tr>
                        <td><span  style={{color:"#000000", fontSize:'3mm'}}>Improvement in Logical Thinking</span></td>
                      </tr>
                    </table>
                  </CRow>
                </CContainer>
                <CContainer style={{textAlign:'center',marginBottom:30}}>
                  <CRow className="justify-content-center" >
                    <span className="h6" style={{color:"#000000"}}>Achievements</span>                        
                  </CRow>
                  <CRow className="justify-content-center" style={{color:'black'}} >
                    {/* <CCol xs="12" sm="8" md="6" xl="4" style={{padding:0}}>                     */}
                      <div style={{borderWidth:'1px',borderStyle:"solid",borderColor:"#ced2d8",borderRadius:"0.6rem",width:'5.5rem',height:'5.5rem'}}>
                        <CRow className="justify-content-center" style={{height:'3rem', alignItems:'flex-end'}}><CIcon name="cib-skype" style={{width:"2.5rem",height:"2.5rem"}} /></CRow>
                        <CRow className="justify-content-center" style={{height:'2.5rem',alignItems:'center'}}><span  style={{color:"#454545", fontSize:'2.5mm', width:"5rem"}}>Introduction to Coding</span></CRow>
                      </div>
                    {/* </CCol> */}
                  </CRow>
                </CContainer>

                <CContainer style={{textAlign:'center',marginBottom:20}}>
                  <CRow className="justify-content-center" >
                    <span className="h2" style={{color:"#fe9953"}}>US$249</span>                        
                  </CRow>
                  <span className="h6" style={{color:"#000000", background:"#fde4d0", padding:5}}>Price Per Class US$ 31</span>
                </CContainer>

                <CContainer style={{textAlign:'center'}}>
                  {/* <CRow className="justify-content-center" >                     */}
                    {/* <CButton size="lg" block style={{color:"#ffffff",background:"#fe9953", marginLeft:30,marginRight:30}}  to={`/auth/payment/0`}>BOOK NOW</CButton>                       */}
                    <StripeCheckout
                      name={course1.name}
                      description={course1.description}
                      token={onToken1}
                      stripeKey={stripeKey}
                      amount={course1.amount * 100} // cents
                      currency={course1.currency}
                      image="https://stripe.com/img/documentation/checkout/marketplace.png"
                      zipCode={false}
                      allowRememberMe={false}
                    >
                      <CButton size="lg" block style={{color:"#ffffff",background:course1.isPurchased?'#3399ff':"#fe9953", marginLeft:0,marginRight:0}}>{course1.isPurchased?'GO':'BOOK NOW'}</CButton>
                    </StripeCheckout>
                  {/* </CRow> */}
                  <span  style={{color:"#000000", fontSize:'2.8mm'}}>Cancel Anytime. Same day 100% refund. No questions asked</span>
                </CContainer>
              </CCardBody>
            </CCard>
          </CCol>

          <CCol xs="12" sm="12" md="4" >
            <CCard style={{borderRadius:'1rem', marginLeft:10,marginRight:10}}>
              <CCardHeader style={{borderTopLeftRadius:"1rem",borderTopRightRadius:"1rem",background:"#fff8f5"}}>   
                <CRow className="justify-content-center" style={{textAlign:'center', marginTop:20}}>    
                  <span className="h5" style={{color:"#000000"}}>IOT Intermediate Level</span>   
                </CRow>
                <CRow className="justify-content-center" style={{textAlign:'center'}}>
                  <span className="h6" style={{color:"#000000"}}>48 Classes</span>
                </CRow>
              </CCardHeader>
              <CCardBody style={{paddingLeft:'3px', paddingRight:'3px'}}>
                <CContainer>
                  <CRow className="justify-content-center" style={{textAlign:'center',marginBottom:20}}>
                    <table>
                      <tr>
                        <td><span className="h6" style={{color:"#000000"}}>Curriculum</span></td>
                      </tr>
                      <tr>
                        <td><span  style={{color:"#000000",fontSize:'3mm'}}>Animations, Game Design, Advanced App Structures</span></td>
                      </tr>
                    </table>
                  </CRow>
                </CContainer>
                <CContainer >
                  <CRow className="justify-content-center"  style={{textAlign:'center',marginBottom:20}}>
                    <table>
                      <tr>
                        <td><span className="h6" style={{color:"#000000"}}>Activities</span></td>
                      </tr>
                      <tr>
                        <td><span  style={{color:"#000000", fontSize:'3mm'}}>Create Interactive Apps with Deep UX/UI Interface</span></td>
                      </tr>
                    </table>
                  </CRow>
                </CContainer>
                <CContainer>
                  <CRow className="justify-content-center" style={{textAlign:'center',marginBottom:20}}>
                    <table>
                      <tr>
                        <td><span className="h6" style={{color:"#000000"}}>Cognitive Benefits</span></td>
                      </tr>
                      <tr>
                        <td><span  style={{color:"#000000", fontSize:'3mm'}}>Full Enterpreneur Creative Expression.</span></td>
                      </tr>
                    </table>
                  </CRow>
                </CContainer>
                <CContainer style={{textAlign:'center',marginBottom:30}}>
                  <CRow className="justify-content-center" >
                    <span className="h6" style={{color:"#000000"}}>Achievements</span>                        
                  </CRow>
                  <CRow className="justify-content-center" style={{color:'black'}} >
                    {/* <CCol xs="12" sm="8" md="6" xl="4" style={{padding:0}}>                     */}
