import React, { Component } from 'react'
import {
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CRow, CBadge, CDataTable, CLink
} from '@coreui/react';
import CIcon from '@coreui/icons-react'

import { Link } from 'react-router-dom';

import historyData from './HistoryData.js'

export default function DeviceCommandHistory({match}) {
  const { useState } = React;

  const [currentPage, setCurrentPage] = useState(1)
  const [currentPagination, setCurrentPagination] = useState(5)
  const getLastVal=(currentPage)=>{
    if(currentPage*currentPagination < historyData.length)
      return currentPage*currentPagination;
    return historyData.length;
  }

  const fields = [    
    { key: 'CommandType', _style: { width: '30%'} },        
    { key: 'Time', _style: { width: '30%'} },    
    { key: 'Result',_style: { width: '30%' } },
  ]

  const getResultTheme = (item)=>{
    if(item.Result == 'Failure') 
      return  `<CBadge color='danger'>${item.Result}</CBadge>`

    switch (item.CommandType) {
      case 'Capture Photo': return `<CLink>${item.Result}</CLink>`
      case 'Capture Video': return `<CLink>${item.Result}</CLink>`
    }
  }

  const getColor = (item)=>{
    switch (item.CommandType) {
      case 'Capture Photo': return '#00ff00'
      case 'Capture Video': return '#0000ff'
      default: return '#000000'
    }
  }

  return (
    <div className="animated fadeIn">
      <CRow> 
        <CCol xl="12">
          <CCard custom>
            <CCardHeader>
              Monitor
            </CCardHeader>
            <CCardBody>         
            <CDataTable
              items={historyData}
              fields={fields}
              // columnFilter
              tableFilter
              // footer
              itemsPerPageSelect
              itemsPerPage={currentPagination}
              dark={false}
              hover={false}
              border={true}
              outlined={true}
              loading={false}
              sorter
              // clickableRows
              pagination={{ doubleArrows: true, align: 'center' }}
              // loading
              // onRowClick={(item,index,col,e) => console.log(item,index,col,e)}
              onPageChange={(val) => {
                  console.log('new page:', val);
                  setCurrentPage(val)
                }
              }
              onPagesChange={(val) => 
                console.log('new pages:', val)
              }
              onPaginationChange={(val) => {
                console.log('new pagination:', val);
                setCurrentPagination(val);
              }}
              // onFilteredItemsChange={(val) => console.log('new filtered items:', val)}
              // onSorterValueChange={(val) => console.log('new sorter value:', val)}
              // onTableFilterChange={(val) => console.log('new table filter:', val)}
              // onColumnFilterChange={(val) => console.log('new column filter:', val)}
              scopedSlots = {{
                'Result':
                  (item)=>(
                    <td>
                      {/* {getResultTheme(item)} */}
                      {
                        item.Result == 'Failure'? <CBadge color='danger'>{item.Result}</CBadge> 
                        : <CLink style={{color:getColor(item)}} to='/' >{item.Result}</CLink>
                      }
                    </td>
                  ),
                'CommandType':
                  (item)=>(
                    <td>
                      <spin style={{color:getColor(item)}}>
                        {item.CommandType}
                      </spin>
                    </td>
                  )
              }}
            />
            <div style={{textAlign:'right'}}>{              
              ((currentPage-1)*currentPagination+1) + '-' + getLastVal(currentPage) +' of '+historyData.length
              } 
            </div>
          </CCardBody>
          </CCard>
        </CCol>      
      </CRow>
    </div>      
  )
}
