import DeleteIcon from '@material-ui/icons/Delete';

function TabPanel(props) {
  const { children, value, index } = props;
  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      // id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
    >
      {value === index && (
        <Box p={4}>
          {children}
        </Box>
      )}
    </div>
  );
}

function Alert(props) {
  return <MuiAlert elevation={6} {...props} />;
}

function preventDefault(event) {
  event.preventDefault();
}

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    flexGrow: 1,
  },
  dataTable: {
    marginTop: theme.spacing(3),
  },
  button: {
    margin: theme.spacing(1),
  }
}));

export default function Courses() {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);
  const [categoryList, setCategoryList] = React.useState([]);
  const [courseList, setCourseList] = React.useState([]);
  const [levelList, setLevelList] = React.useState([]);
  const [courseTitle, setCourseTitle] = React.useState('');
  const [courseDescription, setCourseDescription] = React.useState('');
  const [level, setLevel] = React.useState(0);
  const [category, setCategory] = React.useState(0);
  const [image, setImage] = React.useState(null);
  const [imageHeight, setImageHeight] = React.useState('0px');
  const [toast, showToast] = React.useState(false);
  const [toastMessage, setToastMessage] = React.useState('');
  const [toastClass, setToastClass] = React.useState('success');
  const [loading, setLoading] = React.useState(false);
  const [imageFile, setImageFile] = React.useState('');

  React.useEffect(() => {
    getCategoryList();
    setLevelList([{levelId:1, name:'Beginner'},{levelId:2, name:'Intermediate'},{levelId:3, name:'Expert'}]);
  }, []);

  const getCategoryList = async () => {
    const dataList = await axios({
      url: 'https://hfa3ovai45.execute-api.us-east-2.amazonaws.com/api/category',
      method: 'get'
    }).then(response => response.data).then((data) => {
      return data;
    });
    if(dataList) { setCategoryList(dataList); getCourseList(); }
  };

  const getCourseList = async () => {
    const dataList = await axios({
      url: 'https://hfa3ovai45.execute-api.us-east-2.amazonaws.com/api/course',
      method: 'get'
    }).then(response => response.data).then((data) => {
      return data;
    });
    if(dataList) { setCourseList(dataList); }
  };

  function deleteCourse(courseId) {
    axios({
      url: 'https://hfa3ovai45.execute-api.us-east-2.amazonaws.com/api/course',
      method: 'delete',
      data: {
        'courseId': courseId
      },
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(response => response.data).then((data) => {
      setToastClass('success');
      setToastMessage('Success: ' + data);
      showToast(true);
    });
  }

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  function fileUpload(file) {
    const formData = new FormData();
    formData.append('file', file);
    var re = /(?:\.([^.]+))?$/;
    var ext = re.exec(file.name)[1];
    axios({
      url: 'https://hfa3ovai45.execute-api.us-east-2.amazonaws.com/api/upload?ext='+ext,
      method: 'put',
      headers: {
        'Content-Type': 'multipart/form-data',
      },
      data: formData
    }).then(response => response.data).then((data) => {
      console.log('data===>'+JSON.stringify(data));
      if(data) {
        setImageFile(data.filename);
        setImageHeight("125px");
      }
      setLoading(false);
    });
  }

  function setThumbnail(event) {
    setLoading(true);
    setImage(URL.createObjectURL(event.target.files[0]));
    fileUpload(event.target.files[0]);
  };

  function handleSubmit(event) {
    event.preventDefault();
    const formData = new FormData();
    formData.append('file', image);
    axios({
      url: 'https://hfa3ovai45.execute-api.us-east-2.amazonaws.com/api/course',
      method: 'post',
      data: {
        'title': courseTitle,
        'description': courseDescription,
        'category': category,
        'level': level,
        'imageFile': imageFile
      },
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(response => response.data).then((data) => {
      setToastClass('success');
      setToastMessage('Success: ' + data);
      showToast(true);
      setCourseTitle('');
      setCourseDescription('');
      setCategory(0);
      setImage(null);
      setImageHeight('0px');
      setImageFile('');
    });
  }

  return (
    <React.Fragment>
      <div className={classes.root}>
        <Paper>
          <Tabs indicatorColor="primary" textColor="primary" value={value} onChange={handleChange} variant="fullWidth">
            <Tab label="Courses" />
            <Tab label="Add Course" />
          </Tabs>
          <TabPanel value={value} index={0}>
            <Table size="small">
              <TableHead>
                <TableRow>
                  <TableCell style={{fontWeight:'bold'}}>Title</TableCell>
                  <TableCell style={{fontWeight:'bold'}}>Category</TableCell>
                  <TableCell style={{fontWeight:'bold'}}>Level</TableCell>
                  <TableCell></TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {courseList.map((row) => (
                  <TableRow key={row.courseId}>
                    <TableCell>{row.title}</TableCell>
                    <TableCell>
                      { row.category !== 0 &&
                        categoryList.find(cat => cat.categoryId === row.category).name
                      }
                    </TableCell>
                    <TableCell>
                      { row.level !== 0 &&
                        levelList.find(lvl => lvl.levelId === row.level).name
                      }
                    </TableCell>
                    <TableCell align="right">
                      <IconButton size="small" aria-label="Delete" onClick={(e) => { e.preventDefault(); deleteCourse(row.courseId); }}>
                        <DeleteIcon fontSize="small" />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TabPanel>
          <TabPanel value={value} index={1}>
            <form onSubmit={handleSubmit}>
            <Grid container spacing={3} alignItems="center">
              <Grid item xs={12}>
                <TextField
                  label="Course Title"
                  size="small"
                  variant="outlined"
                  onChange={e => setCourseTitle(e.target.value)}
                  value={courseTitle}
                  fullWidth
                  required
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  label="Course Description"
                  size="small"
                  variant="outlined"
                  onChange={e => setCourseDescription(e.target.value)}
                  value={courseDescription}
                  fullWidth
                  required
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <FormControl required fullWidth size="small" variant="outlined" className={classes.formControl}>
                  <InputLabel id="categoryLabel">Category</InputLabel>
                  <Select
                    labelId="categoryLabel"
                    id="category"
                    label="Parent Category"
                    onChange={e => setCategory(e.target.value)}
                    value={category}
                  >
                    {categoryList.map((category) => (
                      <MenuItem value={category.categoryId}>{category.name}</MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} sm={6}>
                <FormControl required fullWidth size="small" variant="outlined" className={classes.formControl}>
                  <InputLabel id="levelLabel">Level</InputLabel>
                  <Select
                    labelId="levelLabel"
                    id="level"
                    label="level"
                    onChange={e => setLevel(e.target.value)}
                    value={level}
                  >
                    <MenuItem value={1}>Beginner</MenuItem>
                    <MenuItem value={2}>Intermediate</MenuItem>
                    <MenuItem value={3}>Expert</MenuItem>
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={12} sm={6} md={6} lg={3}>
                <Button variant="contained" component="label">
                  <PhotoLibraryIcon /> &nbsp; Upload Image
                  <input type="file" accept="image/*" style={{display:"none"}} onChange={setThumbnail}/>
                </Button>
              </Grid>
              <Grid item xs={12} sm={6} md={6} lg={3}
                component={Paper}
                style={{backgroundImage:`url(${image})`,height:`${imageHeight}`,backgroundRepeat:'no-repeat',backgroundPosition: 'center',}}>
                <Fade in={loading}><CircularProgress /></Fade>
              </Grid>
              <Grid item xs={12} sm={6} md={6} lg={3}>
                <Button variant="contained" component="label">
                  <VideoLibraryIcon /> &nbsp; Upload Video
                  <input type="file" accept="video/*" style={{display:"none"}} onChange={setThumbnail}/>
                </Button>
              </Grid>
              <Grid item xs={12} sm={6} md={6} lg={3}
                component={Paper}>
              </Grid>
              <Grid item xs={12} sm={6} md={6} lg={3}>
                <Button fullWidth type="submit" variant="contained" color="primary">
                  <SaveIcon fontSize="small" /> &nbsp; Save
                </Button>
              </Grid>
            </Grid>
            </form>
          </TabPanel>
          <Snackbar
            anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
            open={toast}
            autoHideDuration={3000}
            onClose={() => { showToast(false); getCategoryList(); }}>
            <Alert
              severity={toastClass}
              action={
                <IconButton
                  aria-label="close"
                  color="inherit"
                  size="small"
                  onClick={() => { showToast(false); getCategoryList(); }}
                >
                  <CloseIcon fontSize="inherit" />
                </IconButton>
              }
            >
              {toastMessage}
            </Alert>
          </Snackbar>
        </Paper>
      </div>
    </React.Fragment>
  );
}
