import React from 'react';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Box from '@material-ui/core/Box';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import MenuIcon from '@material-ui/icons/Menu';
import Button from '@material-ui/core/Button';
import { NavLink as RouterLink, Route } from "react-router-dom";
import List from '@material-ui/core/List';
import Drawer from '@material-ui/core/Drawer';
import Hidden from '@material-ui/core/Hidden';
import Divider from '@material-ui/core/Divider';
import Paper from '@material-ui/core/Paper';
import clsx from 'clsx';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';

import { useStyles } from '../../Layout/Theme/DashboardTheme';
import Copyright from '../../Layout/Copyright';
import Courses from './Courses';
import Categories from './Categories';
import Deposits from './Deposits';
import { dashboardMenuItems } from './MenuItems';
import Logo from '../../../assets/images/logo.png';

export default function Admin() {
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);

  const drawer = (
    <div>
      <Toolbar className={classes.toolbar}>
        <img src={Logo} alt="Logo" style={{width: "70%", marginRight: "auto", marginLeft: "auto"}} />
      </Toolbar>
      <Divider />
      <List>{dashboardMenuItems}</List>
      <Divider />
    </div>
  );

  const container = window !== undefined ? () => window.document.body : undefined;
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);

  return (
    // <div className={classes.root}>
    <div className={classes.root}>
      <AppBar position="fixed" className={classes.appBar}>
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            edge="start"
            onClick={() => setOpen(!open)}
            className={classes.menuButton}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" noWrap>
            Dashboard
          </Typography>
          <Box flexGrow={1} />
          <Button edge="end" color="inherit" component={RouterLink} to="/">
            <ExitToAppIcon fontSize="small" /> &nbsp; Logout
          </Button>
        </Toolbar>
      </AppBar>

      <nav className={classes.drawer} aria-label="mailbox folders">
        <Hidden mdUp implementation="css">
          <Drawer
            container={container}
            variant="temporary"
            anchor={theme.direction === 'rtl' ? 'right' : 'left'}
            open={open}
            onClose={() => setOpen(!open)}
            classes={{paper: classes.drawerPaper,}}
            ModalProps={{keepMounted: true,}}>
            {drawer}
          </Drawer>
        </Hidden>
        <Hidden smDown implementation="css">
          <Drawer classes={{ paper: classes.drawerPaper,}} variant="permanent" open>
            {drawer}
          </Drawer>
        </Hidden>
      </nav>

      <main className={classes.content}>
        <div className={classes.appBarSpacer} />
        <Container maxWidth="lg" className={classes.container}>
          <Route path="/admin" exact>
            <Grid container spacing={3}>
              <Grid item xs={12} md={4} lg={3}>
                <Paper className={fixedHeightPaper}>
                  <Deposits />
                </Paper>
              </Grid>
            </Grid>
          </Route>
          <Route path="/admin/categories"><Categories /></Route>
          <Route path="/admin/courses"><Courses /></Route>
          <Box pt={4}>
            <Copyright />
          </Box>
        </Container>
      </main>
    </div>
  );
}
