import React, { Component } from 'react'
import MaterialTable from 'material-table'
import { forwardRef } from 'react';

import AddBox from '@material-ui/icons/AddBox';
import ArrowDownward from '@material-ui/icons/ArrowDownward';
import Check from '@material-ui/icons/Check';
import ChevronLeft from '@material-ui/icons/ChevronLeft';
import ChevronRight from '@material-ui/icons/ChevronRight';
import Clear from '@material-ui/icons/Clear';
import DeleteOutline from '@material-ui/icons/DeleteOutline';
import Edit from '@material-ui/icons/Edit';
import FilterList from '@material-ui/icons/FilterList';
import FirstPage from '@material-ui/icons/FirstPage';
import LastPage from '@material-ui/icons/LastPage';
import Remove from '@material-ui/icons/Remove';
import SaveAlt from '@material-ui/icons/SaveAlt';
import Search from '@material-ui/icons/Search';
import ViewColumn from '@material-ui/icons/ViewColumn';

import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import Divider from '@material-ui/core/Divider';
import { Grid } from '@material-ui/core';
import Icon from '@material-ui/core/Icon';
import { Collapse } from '@material-ui/core';
import clsx from 'clsx';
import IconButton from '@material-ui/core/IconButton';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import DeviceSensorCard from "../components/DeviceSensorCard"


const tableIcons = {
    Add: forwardRef((props, ref) => <AddBox {...props} ref={ref} />),
    Check: forwardRef((props, ref) => <Check {...props} ref={ref} />),
    Clear: forwardRef((props, ref) => <Clear {...props} ref={ref} />),
    Delete: forwardRef((props, ref) => <DeleteOutline {...props} ref={ref} />),
    DetailPanel: forwardRef((props, ref) => <ChevronRight {...props} ref={ref} />),
    Edit: forwardRef((props, ref) => <Edit {...props} ref={ref} />),
    Export: forwardRef((props, ref) => <SaveAlt {...props} ref={ref} />),
    Filter: forwardRef((props, ref) => <FilterList {...props} ref={ref} />),
    FirstPage: forwardRef((props, ref) => <FirstPage {...props} ref={ref} />),
    LastPage: forwardRef((props, ref) => <LastPage {...props} ref={ref} />),
    NextPage: forwardRef((props, ref) => <ChevronRight {...props} ref={ref} />),
    PreviousPage: forwardRef((props, ref) => <ChevronLeft {...props} ref={ref} />),
    ResetSearch: forwardRef((props, ref) => <Clear {...props} ref={ref} />),
    Search: forwardRef((props, ref) => <Search {...props} ref={ref} />),
    SortArrow: forwardRef((props, ref) => <ArrowDownward {...props} ref={ref} />),
    ThirdStateCheck: forwardRef((props, ref) => <Remove {...props} ref={ref} />),
    ViewColumn: forwardRef((props, ref) => <ViewColumn {...props} ref={ref} />)
  };

  const useStyles = makeStyles((theme) => ({
    container: {
      display: 'grid',
      gridTemplateColumns: 'repeat(12, 1fr)',
      gridGap: theme.spacing(3),
    },
    paper: {
      padding: theme.spacing(1),
      textAlign: 'center',
      color: theme.palette.text.secondary,
      whiteSpace: 'nowrap',
      marginBottom: theme.spacing(1),
    },
    divider: {
      margin: theme.spacing(2, 0),
    },
    expand: {
      transform: 'rotate(0deg)',
      marginLeft: 'auto',
      transition: theme.transitions.create('transform', {
        duration: theme.transitions.duration.shortest,
      }),
    },
    expandOpen: {
      transform: 'rotate(180deg)',
    },
  }));

export default function DeviceDashboard() {
  const { useState } = React;
  const [selectedRow, setSelectedRow] = useState(null);
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(true);
  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  const [columns, setColumns] = useState([
    { title: 'No', field: 'no', editable: 'onUpdate' },
    { title: 'IMEI', field: 'imei', editable: 'onUpdate' },
    {
      title: 'Customer',
      field: 'customer',
      lookup: { 34: 'Jane', 63: 'John' },
    },
    { title: 'Device Name', field: 'deviceName', editable: 'onUpdate' },
    { title: 'Device Status', field: 'deviceStatus', editable: 'onUpdate' },
    { title: 'HeartBeats', field: 'heartBeats', editable: 'onUpdate' },
  ]);
  
  const [data, setData] = useState([
    { no: '1', imei: '861475030047510', customer: 34, deviceName:'Raspberry PI 3', deviceStatus:'Online', heartBeats:'2hours 30mins' },
    { no: '2', imei: '863158020785889', customer: 63, deviceName:'Raspberry PI 3', deviceStatus:'Online', heartBeats:'2hours 30mins' },
    { no: '3', imei: '863158020785889', customer: 63, deviceName:'Raspberry PI 3', deviceStatus:'Offline', heartBeats:'2hours 30mins' },
    { no: '4', imei: '863158020785889', customer: 63, deviceName:'Raspberry PI 3', deviceStatus:'Offline', heartBeats:'2hours 30mins' },
    { no: '5', imei: '863158020785889', customer: 63, deviceName:'Raspberry PI 3', deviceStatus:'Online', heartBeats:'2hours 30mins' },
    { no: '6', imei: '863158020785889', customer: 63, deviceName:'Arduino UO', deviceStatus:'Online', heartBeats:'2hours 30mins' },
    { no: '7', imei: '863158020785889', customer: 63, deviceName:'Arduino UO', deviceStatus:'Offline', heartBeats:'2hours 30mins' },
    { no: '8', imei: '863158020785889', customer: 63, deviceName:'Arduino UO', deviceStatus:'Offline', heartBeats:'2hours 30mins' },
    { no: '9', imei: '863158020785889', customer: 63, deviceName:'Arduino UO', deviceStatus:'Online', heartBeats:'2hours 30mins' },
    { no: '10', imei: '863158020785889', customer: 63,deviceName:'Arduino UO', deviceStatus:'Online', heartBeats:'2hours 30mins' },
  ]);

  return (
    <div>
      <Typography variant="h4" color="primary" style={{marginLeft:10, marginBottom:20}}>
        Dashboard
      </Typography>
      <Grid container spacing={2}>
        <Grid item xs={9}>
          <Paper className={classes.paper}>
            <DeviceSensorCard name="MymYMY"/>
            <DeviceSensorCard name="MymYMY"/>
          </Paper>
          <MaterialTable
            title="Monitor"
            columns={columns}
            data={data}
            icons={tableIcons}
            onRowClick={((evt, selectedRow) => setSelectedRow(selectedRow.tableData.id))}
            options={{
              headerStyle: {
                backgroundColor: '#039be5',
                color: '#FFF'
              },
              pageSize:4,
              pageSizeOptions:[4,8,12],
              // rowStyle: rowData => ({
              //   // backgroundColor: (selectedRow === rowData.tableData.id) ? '#EEE' : '#FFF'
              // })
            }}           
          />
        </Grid>
        <Grid item xs={3}>
          <Paper className={classes.paper}>
            <Typography variant="h7" color="primary" style={{marginLeft:0, marginBottom:0}}>
              Commands
            </Typography>
            <IconButton
              className={clsx(classes.expand, {
                [classes.expandOpen]: expanded,
              })}
              onClick={handleExpandClick}
              aria-expanded={expanded}
              aria-label="show more"
            >
              <ExpandMoreIcon/>
            </IconButton>        
            <Collapse in={expanded} timeout="auto" unmountOnExit>            
              <Paper className={classes.paper} startIcon={<Icon>send</Icon>} style={{cursor:"pointer"}}>Capture Photo</Paper>
              <Paper className={classes.paper} endIcon={<Icon>send</Icon>} style={{cursor:"pointer"}}>Capture Video</Paper>
              <Paper className={classes.paper} endIcon={<Icon>send</Icon>} style={{cursor:"pointer"}}>Get Device Status</Paper>
              <Paper className={classes.paper} endIcon={<Icon>send</Icon>} style={{cursor:"pointer"}}>
                Get Device Status
              </Paper>
            </Collapse>
          </Paper>
        </Grid>
      </Grid>
    </div>
  )
}
