import React from 'react';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Link from '@material-ui/core/Link';
import { NavLink as RouterLink, Route } from "react-router-dom";
import Paper from '@material-ui/core/Paper';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';

import Navigation from '../../Layout/Navigation';
import Copyright from '../../Layout/Copyright';
import { useStyles } from '../../theme/PageTheme';

import axios from 'axios';

export default function Home() {
  const classes = useStyles();
  const [courseList, setCourseList] = React.useState([]);

  React.useEffect(() => {
    getCourseList();
  }, []);

  const getCourseList = async () => {
    const dataList = await axios({
      url: 'https://hfa3ovai45.execute-api.us-east-2.amazonaws.com/api/course',
      method: 'get'
    }).then(response => response.data).then((data) => {
      return data;
    });
    if(dataList) { setCourseList(dataList); }
  };

  return (
    <div className={classes.root}>
      <Navigation />
      <main>
        <Container className={classes.mainContent}>
          <Grid container direction="row" alignItems="center" justify="center">
            <Grid item xs={12} sm={8} md={5}>
              <Box p={3}>
                <Typography variant="h4" color="primary">Learn new skills,</Typography>
                <Typography variant="h4" gutterBottom>gain more experience.</Typography>
                <Typography variant="subtitle1" gutterBottom>
                  Our mission is to spread education that is easy accessible and everyone can learn.
                </Typography>
              </Box>
            </Grid>
            <Grid item xs={false} sm={4} md={7} component={Paper} elevation={6} className={classes.backImage} />
          </Grid>
        </Container>
        <Container className={classes.cardGrid}>
          <Paper>
            <Box p={3}>
              <Typography component="h3" variant="h6" gutterBottom>
                Browse our <span style={{color:"#F57C00"}}>popular courses</span>
              </Typography>
              <Grid container spacing={3}>
                {courseList.map((course) => (
                  <Grid item key={course.courseId} xs={12} sm={6} md={3}>
                    <Card className={classes.card}>
                      <CardMedia
                        className={classes.cardMedia}
                        image="https://source.unsplash.com/random"
                        title="Image title"
                      />
                      <CardContent className={classes.cardContent}>
                        <Typography gutterBottom variant="h5" component="h2">
                          {course.title}
                        </Typography>
                        <Typography>
                          {course.description}
                        </Typography>
                      </CardContent>
                      <CardActions>
                        <Button size="small" variant="contained" color="primary">
                          View Details
                        </Button>
                      </CardActions>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Box>
          </Paper>
        </Container>
      </main>
      <footer className={classes.footer}>
        <Copyright />
      </footer>
    </div>
  );
}
