import React from 'react';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import SendIcon from '@material-ui/icons/Send';
import { Divider } from '../../node_modules/@material-ui/core';
import DeviceSensorHistory from '../components/DeviceSensorHistory'

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  details: {
    display: 'flex',
    flexDirection: 'column',
  },
  content: {
    flex: '1 0 auto',
  },
  cover: {
    width: 151,
  },
  controls: {
    display: 'flex',
    alignItems: 'center',
    paddingLeft: theme.spacing(1),
    paddingBottom: theme.spacing(1),
  },
}));


export default function DeviceSensorCard(props) {
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };


  return (
    <React.Fragment>
    <Card style={{background:props.backcolor}}>
      <div className={classes.details}>
        <CardContent className={classes.content}>
          <Typography component="h5" variant="h5" style={{color:"#ffffff"}} >
            {props.name}
          </Typography>
          <Typography variant="subtitle1" color="textSecondary" style={{color:"#ffffff"}}>
            {props.count}
          </Typography>
        </CardContent>
        <Divider/>
        <div style={{cursor:"pointer", padding:5, background:props.morebgcolor }} className="flex-container" onClick={handleClickOpen}>
          <span style={{color:"#ffffff"}} className="row">More Info</span>
          <SendIcon style={{color:"#ffffff",width:20,marginLeft:10,marginRight:10}}/>
        </div>
      </div>
      <style>{`
              
        .flex-container {
          padding: 0;
          margin: 0;
          list-style: none;
          
          display: -webkit-box;
          display: -moz-box;
          display: -ms-flexbox;
          display: -webkit-flex;
          display: flex;
            align-items: center;
            justify-content: center;

        }

        row{
          width: 100%;
        }

        .flex-item {
          background: tomato;
          padding: 5px;
          width: 200px;
          height: 150px;
          margin: 10px;
          
          line-height: 150px;
          color: white;
          font-weight: bold;
          font-size: 3em;
          text-align: center;
        }
      `}</style>

