import React, { Component, useEffect } from 'react'
import {
  CButton,
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CRow, CDataTable, CForm, CFormGroup, CFormText, CLink, CInput, CLabel, CSelect
} from '@coreui/react';
import CIcon from '@coreui/icons-react'
import { Formik } from 'formik'
import * as Yup from 'yup'

import devicesData from './DevicesData.js'
import _sensorsData from './SensorsData.js'

export default function Sensors({match}) {
  const { useState } = React;
  const [currentPage, setCurrentPage] = useState(1)
  const [currentPagination, setCurrentPagination] = useState(5)
  const getLastVal = (currentPage) => {
    if (currentPage * currentPagination < sensorsData.length)
      return currentPage * currentPagination;
    return sensorsData.length;
  }
  const fields = [
    { key: 'SensorType', _style: { width: '20%' } },
    { key: 'HighThreshold', _style: { width: '20%' } },
    { key: 'LowThreshold', _style: { width: '20%' } },
    {
      key: 'actions',
      label: '',
      _style: { width: '15%' },
      sorter: false,
      filter: false
    },
  ]

  const [sensorsData, setSensorsData]  = useState([])
  useEffect(() => {
    setSensorsData(_sensorsData.map(a => ({...a})))
  }, [])
  const [isAddSensor, setIsAddSensor] = useState(true)
  const [editId, setEditId] = useState(-1)

  const removeSensor = (item) => {
    handleReset()
    const sensor = sensorsData.find(sensor => sensor.id === item.id)
    //console.log(item.id)
    //console.log(device)    
    var index = sensorsData.indexOf(sensor)
    if (index !== -1) {
      sensorsData.splice(index, 1);
    }
    console.log(sensorsData.length);

    setSensorsData(sensorsData.map(a => ({...a})))
  }

  const editSensor = (item) => {
    document.getElementById('imei-input').value = item.IMEI;
    setIsAddSensor(false)
    setEditId(item.id)
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    alert(JSON.stringify(document.getElementById('sensortype-input').value, null, 2))

    const sensor = sensorsData.find(sensor => sensor.id === editId)
    var index = sensorsData.indexOf(sensor)
    const sensortype = document.getElementById('sensortype-input').value
    const highthreshold =  document.getElementById('highthreshold-select').value
    const lowthreshold =  document.getElementById('lowthreshold-select').value

    if (index !== -1) {
      sensorsData[index].SensorType = sensortype       
      sensorsData[index].HighThreshold = highthreshold        
      sensorsData[index].LowThreshold = lowthreshold        
    } else {
      sensorsData.push({id:(sensorsData.length),SensorType:sensortype, HighThreshold:highthreshold, LowThreshold:lowthreshold })
    }
    setSensorsData(sensorsData.map(a => ({...a})))    
    handleReset()
    // console.log('Device has been successfully saved!', sensorsData)
  }

  const handleReset = () => {
    document.getElementById('sensortype-input').value = ''
    setIsAddSensor(true)
    setEditId(-1)
  }

  const device = devicesData.find( device => device.id.toString() === match.params.id)

  return (
    <div className="animated fadeIn">
      <CRow>
        <CCol xl="7">
          <CCard custom>
            <CCardHeader >
              <h5 >Sensor List </h5> {'( imei: '+device.IMEI+', device type: '+device.DeviceType+' )'}
            </CCardHeader>
            <CCardBody>
              <CDataTable
                items={sensorsData}
                fields={fields}
                // columnFilter
                tableFilter
                // footer
                itemsPerPageSelect
                itemsPerPage={currentPagination}
                dark={false}
                hover={false}
                border={true}
                outlined={true}
                loading={false}
                sorter
                // clickableRows
                pagination={{ doubleArrows: true, align: 'center' }}
                // loading
                // onRowClick={(item,index,col,e) => {console.log(item,index,col,e)}}
                onPageChange={(val) => {
                  console.log('new page:', val);
                  setCurrentPage(val)
                }
                }
                // onPagesChange={(val) => console.log('new pages:', val)}
                onPaginationChange={(val) => {
                  console.log('new pagination:', val);
                  setCurrentPagination(val);
                }}
                // onFilteredItemsChange={(val) => console.log('new filtered items:', val)}
                // onSorterValueChange={(val) => console.log('new sorter value:', val)}
                // onTableFilterChange={(val) => console.log('new table filter:', val)}
                // onColumnFilterChange={(val) => console.log('new column filter:', val)}
                scopedSlots={{
                  'IMEI':
                    (item) => (
                      <td>
                        <CLink to={`/modules/devicemanagement/devices/${item.id}`}>{item.IMEI}</CLink>
                      </td>
                    ),
                  'actions':
                    item => {
                      return (
                        <td className="py-2">
                          {
                            editId != item.id ?
                              <CButton color="light" onClick={(e) => { e.preventDefault(); editSensor(item) }}><CIcon name="cil-pencil" /></CButton>
                              : <CButton color="light" variant="outline" disabled><CIcon name="cil-pencil" /></CButton>
                          }
                          <CButton color="light"  onClick={(e) => { e.preventDefault(); removeSensor(item) }}><CIcon name="cil-trash" /></CButton>
                        </td>
                      )
                    }
                }}
              />
              <div style={{ textAlign: 'right' }}>{
                ((currentPage - 1) * currentPagination + 1) + '-' + getLastVal(currentPage) + ' of ' + sensorsData.length
              }
              </div>
            </CCardBody>
          </CCard>
        </CCol>
        <CCol xl="5">
          <CCard custom>
            <CCardHeader id="headingOne" style={{ display: 'inline-flex' }}>
              {
                isAddSensor ? 'Add Sensor' : 'Edit Sensor'
              }
            </CCardHeader>
            <CCardBody>
              <CForm onSubmit={handleSubmit} className="form-horizontal" name='simpleForm'>
                {/* onSubmit={handleSubmit} */}
                <CFormGroup row>
                  <CCol md="4">
                    <CLabel htmlFor="imei-input">IMEI</CLabel>
                  </CCol>
                  <CCol xs="12" md="8">
                    <CInput type="text"
                      name="imei-input"
                      id="imei-input"
                      placeholder="Please input imei"
                      autoComplete="given-name"
                      required />
                  </CCol>
                </CFormGroup>
                <CFormGroup row>
                  <CCol md="4">
                    <CLabel htmlFor="select">Device Type</CLabel>
                  </CCol>
                  <CCol xs="12" md="8">
                    <CSelect custom name="select" id="select">
                      <option id="devicetype-select" value="Raspberry PI">Raspberry PI</option>
                    </CSelect>
                  </CCol>
                </CFormGroup>
                <hr />
                <CButton type="submit" variant="outline" className="mr-1" color="primary" block><CIcon name="cil-save" /> Save</CButton>
                <CButton type="reset" variant="outline" className="mr-1" color="danger" onClick={handleReset} block><CIcon name="cil-ban" /> Reset</CButton>
              </CForm>
            </CCardBody>
          </CCard>
        </CCol>
      </CRow>
    </div>
  )
}
