import React from 'react';

import MaterialTable from 'material-table'

export default function Devices() {
  const { useState } = React;

  const [columns, setColumns] = useState([
    { title: 'No', field: 'no', editable: 'onUpdate' },
    { title: 'IMEI', field: 'imei', editable: 'onUpdate' },
    {
      title: 'Customer',
      field: 'customer',
      lookup: { 34: 'Jane', 63: 'John' },
    },
    { title: 'Sim Card', field: 'simCard', editable: 'onUpdate' },
    { title: 'Device Name', field: 'deviceName', editable: 'onUpdate' },
  ]);
  
  const [data, setData] = useState([
    { no: '1', imei: '861475030047510', customer: 34, simCard: '111', deviceName:'Raspberry PI 3' },
    { no: '2', imei: '863158020785889', customer: 63, simCard: '1', deviceName:'Arduino UO' },
  ]);

  return (
    <div >
    <MaterialTable
      title="Device List"
      columns={columns}
      data={data}
      editable={{
        onRowAdd: newData =>
          new Promise((resolve, reject) => {
            setTimeout(() => {
              setData([...data, newData]);

              resolve();
            }, 1000)
          }),
        onRowUpdate: (newData, oldData) =>
          new Promise((resolve, reject) => {
            setTimeout(() => {
              const dataUpdate = [...data];
              const index = oldData.tableData.id;
              dataUpdate[index] = newData;
              setData([...dataUpdate]);

              resolve();
            }, 1000)
          }),
        onRowDelete: oldData =>
          new Promise((resolve, reject) => {
            setTimeout(() => {
              const dataDelete = [...data];
              const index = oldData.tableData.id;
              dataDelete.splice(index, 1);
              setData([...dataDelete]);

              resolve();
            }, 1000)
          }),
      }}
    />
    </div>
  )
}
