import React from "react";
import {
  CButton,
  CCard,
  CCardBody,
  CCardFooter,
  CCol,
  CContainer,
  CForm,
  CInput,
  CInputGroup,
  CInputGroupPrepend,
  CInputGroupText,
  CRow
} from '@coreui/react'
import CIcon from '@coreui/icons-react'

import { NavLink as RouterLink, Route, useHistory } from "react-router-dom";
import Amplify, { Auth } from 'aws-amplify';

// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
import InputAdornment from "@material-ui/core/InputAdornment";
import Checkbox from "@material-ui/core/Checkbox";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Icon from "@material-ui/core/Icon";

// @material-ui/icons
import Timeline from "@material-ui/icons/Timeline";
import Code from "@material-ui/icons/Code";
import Group from "@material-ui/icons/Group";
import Face from "@material-ui/icons/Face";
import Email from "@material-ui/icons/Email";
import LockOutlined from "@material-ui/icons/LockOutlined";
import Check from "@material-ui/icons/Check";


// core components
import GridContainer from "../../Layout/Grid/GridContainer.js";
import GridItem from "../../Layout/Grid/GridItem.js";
import Button from "../../Layout/CustomButtons/Button.js";
import CustomInput from "../../Layout/CustomInput/CustomInput.js";
import InfoArea from "../../Layout/InfoArea/InfoArea.js";
import Card from "../../Layout/Card/Card.js";
import CardBody from "../../Layout/Card/CardBody.js";

import styles from "../../../assets/material/jss/material-dashboard-pro-react/views/registerPageStyle";

const useStyles = makeStyles(styles);

export default function RegisterPage() {
  const classes = useStyles();
  const history = useHistory();
  const [firstname, setFirstname] = React.useState('');
  const [lastname, setLastname] = React.useState('');
  const [email, setEmail] = React.useState('');
  const [password, setPassword] = React.useState('');

  Amplify.configure({
    Auth: {
      region: process.env.REACT_APP_AWS_REGION,
      userPoolId: process.env.REACT_APP_AWS_USER_POOL_ID,
      userPoolWebClientId: process.env.REACT_APP_AWS_USER_POOL_CLIENT_ID
    }
  });

  async function handleSubmit(event) {
    event.preventDefault();
    try {
      const { userConfirmed } = await Auth.signUp({
        username: email,
        password: password,
        attributes: {
          name: firstname + ' ' + lastname
        }
      });
      if(!userConfirmed) {
        localStorage.setItem('userEmail', email);
        history.push('/auth/verify');
      }
      //console.log(user);
      setFirstname('');
      setLastname('');
      setEmail('');
      setPassword('');
    } catch (error) {
      console.log('error signing up:', error);
    }
  }

  return (
    <div className={classes.container}>
      <CContainer>
        <CRow className="justify-content-center">
          <CCol md="9" lg="7" xl="6">
            <CCard className="mx-4">
              <CCardBody className="p-4">
                <CForm>
                  <h1 style={{color:"#3c4b64"}}>Register</h1>
                  <p className="text-muted">Create your account</p>
                  <CRow className="justify-content-center">
                    <CCol md="6" lg="6" xl="6">
                      <CInputGroup className="mb-3">
                        <CInputGroupPrepend>
                          <CInputGroupText>
                            <CIcon name="cil-user" />
                          </CInputGroupText>
                        </CInputGroupPrepend>
                        <CInput 
                          type="text" 
                          placeholder="First Name" 
                          autoComplete="firstname" 
                          onChange={e => setFirstname(e.target.value)}
                          required
                          value={firstname}
                          />
                      </CInputGroup>
                    </CCol>
                    <CCol md="6" lg="6" xl="6">
                      <CInputGroup className="mb-3">
                        <CInput 
                          type="text" 
                          placeholder="Last Name" 
                          autoComplete="lastname" 
                          onChange={e => setLastname(e.target.value)}
                          required
                          value={lastname}
                        />
                      </CInputGroup>
                    </CCol>
                  </CRow>
                  <CInputGroup className="mb-3">
                    <CInputGroupPrepend>
                      <CInputGroupText>@</CInputGroupText>
                    </CInputGroupPrepend>
                    <CInput 
                      type="email" 
                      placeholder="Email" 
                      autoComplete="email" 
                      onChange={e => setEmail(e.target.value)}
                      required
                      value={email}
                    />
                  </CInputGroup>
                  <CInputGroup className="mb-3">
                    <CInputGroupPrepend>
                      <CInputGroupText>
                        <CIcon name="cil-lock-locked" />
                      </CInputGroupText>
                    </CInputGroupPrepend>
                    <CInput 
                      type="password" 
                      placeholder="Password" 
                      autoComplete="new-password" 
                      onChange={e => setPassword(e.target.value)}
                      required
                      value={password}
                    />
                  </CInputGroup>
                  <CButton type="submit"  color="success" block>Create Account</CButton>
                </CForm>
              </CCardBody>              
            </CCard>
          </CCol>
        </CRow>
      </CContainer>
      {/* <GridContainer justify="center">
        <GridItem xs={12} sm={8} md={6}>
          <Card className={classes.cardSignup} style={{margin:0}}>
            <h2 className={classes.cardTitle}>Register</h2>
            <CardBody>
              <GridContainer justify="center">
                <GridItem xs={12} sm={12} md={12}>                 
                  <form id="signUpForm" className={classes.form} onSubmit={handleSubmit}>
                    <GridContainer>
                      <GridItem xs={12} sm={6}>
                        <CustomInput
                          formControlProps={{
                            fullWidth: true,
                            className: classes.customFormControlClasses
                          }}
                          inputProps={{
                            startAdornment: (
                              <InputAdornment
                                position="start"
                                className={classes.inputAdornment}
                              >
                                <Face className={classes.inputAdornmentIcon} />
                              </InputAdornment>
                            ),
                            onChange: event => {                      
                              setFirstname(event.target.value)
                            },
                            placeholder: "First Name..."
                          }}
                          value={firstname}
                        />
                      </GridItem>
                      <GridItem xs={12} sm={6}>
                          <CustomInput
                            formControlProps={{
                              fullWidth: true,
                              className: classes.customFormControlClasses
                            }}
                            inputProps={{
                              onChange: event => {                      
                                setLastname(event.target.value)
                              },
                              placeholder: "Last Name..."
                            }}
                            value={lastname}
                          />
                      </GridItem>
                    </GridContainer>
                    <CustomInput
                      formControlProps={{
                        fullWidth: true,
                        className: classes.customFormControlClasses
                      }}
                      inputProps={{
                        startAdornment: (
                          <InputAdornment
                            position="start"
                            className={classes.inputAdornment}
                          >
                            <Email className={classes.inputAdornmentIcon} />
                          </InputAdornment>
                        ),
                        onChange: event => {                      
                          setEmail(event.target.value)
                        },
                        type: "email",
                        placeholder: "Email..."
                      }}
                      value={email}
                    />
                    <CustomInput                      
                      formControlProps={{
                        fullWidth: true,
                        className: classes.customFormControlClasses
                      }}
                      inputProps={{
                        startAdornment: (
                          <InputAdornment
                            position="start"
                            className={classes.inputAdornment}
                          >
                            <LockOutlined className={classes.inputAdornmentIcon}>
                              lock_outline
                            </LockOutlined>
                          </InputAdornment>
                        ),
                        onChange: event => {                      
                          setPassword(event.target.value)
                        },
                        type: "password",
                        autoComplete: "off",
                        placeholder: "Password..."
                      }}
                      value={password}
                    />                                      
                    <div className={classes.center} style={{marginTop:30, marginBottom:0}}>
                      <Button type="submit" round color="primary">
                        Get started
                      </Button>
                    </div>
                  </form>
                </GridItem>
              </GridContainer>
            </CardBody>
          </Card>
        </GridItem>
      </GridContainer> */}
    </div>
  );
}
