import React from 'react';
import { Card, CardActions, CardContent, CardMedia, Grid, Typography, Container, Link,
         Paper, Box, Button } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { NavLink as RouterLink, Route } from "react-router-dom";
import axios from 'axios';
import Navigation from '../../../Layout/Navigation';
import Copyright from '../../../Layout/Copyright';
import { useStyles } from '../../../Layout/Theme/PageTheme';

export default function Home() {
  const classes = useStyles();
  const [courseList, setCourseList] = React.useState([]);

  React.useEffect(() => {
    getCourseList();
  }, []);

  const getCourseList = async () => {
    const dataList = await axios({
      url: process.env.REACT_APP_AWS_API_ENDPOINT + '/course',
      method: 'get'
    }).then(response => response.data).then((data) => {
      return data;
    });
    if(dataList) { setCourseList(dataList); }
  };

  return (
    <div className={classes.root}>
      <Navigation />
      <main>
        <Container className={classes.mainContent}>
          <Grid container direction="row" alignItems="center" justify="center">
            <Grid item xs={12} sm={8} md={5}>
              <Box p={3}>
                <Typography variant="h4" color="primary">Learn new skills,</Typography>
                <Typography variant="h4" gutterBottom>gain more experience.</Typography>
                <Typography variant="subtitle1" gutterBottom>
                  Our mission is to spread education that is easy accessible and everyone can learn.
                </Typography>
              </Box>
            </Grid>
            <Grid item xs={false} sm={4} md={7} component={Paper} elevation={6} className={classes.backImage} />
          </Grid>
        </Container>
        <Container className={classes.cardGrid}>
          <Paper>
            <Box p={3}>
              <Typography component="h3" variant="h6" gutterBottom>
                Browse our <span style={{color:"#F57C00"}}>popular courses</span>
              </Typography>
              <Grid container spacing={3}>
                {courseList.map((course) => (
                  <Grid item key={course.courseId} xs={12} sm={6} md={3}>
                    <Card className={classes.card}>
                      <CardMedia
                        className={classes.cardMedia}
                        image={process.env.REACT_APP_AWS_BUCKET_CLOUDFRONT_URL+'/'+course.imageFile}
                        title="Image title"
                      />
                      <CardContent className={classes.cardContent}>
                        <Typography gutterBottom variant="h5" component="h2">
                          {course.title}
                        </Typography>
                        <Typography>
                          {course.description}
                        </Typography>
                      </CardContent>
                      <CardActions>
                        <Button size="small" variant="contained" color="primary" component={RouterLink} to= {`/course/details/${course.courseId}`}>
                          View Details
                        </Button>
                      </CardActions>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            </Box>
          </Paper>
        </Container>
      </main>
      <footer className={classes.footer}>
        <Copyright />
      </footer>
    </div>
  );
}
