import React, { Component } from 'react'
import {
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CRow, CBadge, CDataTable, CAlert,CFormGroup,CInputCheckbox
} from '@coreui/react';
import CIcon from '@coreui/icons-react'

import { Link } from 'react-router-dom';

import historyData from './HistoryData.js'

export default function DeviceCommandHistory({match}) {
  const { useState } = React;

  const [currentPage, setCurrentPage] = useState(1)
  const [currentPagination, setCurrentPagination] = useState(5)
  const getLastVal=(currentPage)=>{
    if(currentPage*currentPagination < historyData.length)
      return currentPage*currentPagination;
    return historyData.length;
  }

  const fields = [    
    { key: 'CommandType', _style: { width: '30%'} },        
    { key: 'Time', _style: { width: '30%'} },    
    { key: 'Result',_style: { width: '30%' } },
  ]

  const getBadge = (status)=>{
    switch (status) {
      case 'Failure': return 'danger'
      case 'Photo': return 'secondary'
      case 'Video': return 'secondary'
      default: return 'secondary'
    }
  }

  return (
    <div className="animated fadeIn">
      <CRow> 
        <CCol xl="12">
          <CCard custom>
            <CCardHeader>
              Monitor
            </CCardHeader>
            <CCardBody>         
            <CDataTable
              items={historyData}
              fields={fields}
              // columnFilter
              tableFilter
              // footer
              itemsPerPageSelect
              itemsPerPage={currentPagination}
              dark={false}
              hover={false}
              border={true}
              outlined={true}
              loading={false}
              sorter
              // clickableRows
              pagination={{ doubleArrows: true, align: 'center' }}
              // loading
              // onRowClick={(item,index,col,e) => console.log(item,index,col,e)}
              onPageChange={(val) => {
                  console.log('new page:', val);
                  setCurrentPage(val)
                }
              }
              onPagesChange={(val) => 
                console.log('new pages:', val)
              }
              onPaginationChange={(val) => {
                console.log('new pagination:', val);
                setCurrentPagination(val);
              }}
              // onFilteredItemsChange={(val) => console.log('new filtered items:', val)}
              // onSorterValueChange={(val) => console.log('new sorter value:', val)}
              // onTableFilterChange={(val) => console.log('new table filter:', val)}
              // onColumnFilterChange={(val) => console.log('new column filter:', val)}
              scopedSlots = {{
                'Status':
                  (item)=>(
                    <td>
                      <CBadge color={getBadge(item.Status)}>
                        {item.Status}
                      </CBadge>
                    </td>
                  ),
                'check_device':
                  item => {
                    return (
                      <td className="py-2">
                        <CFormGroup variant="checkbox" className="checkbox">
                          <CInputCheckbox id={'checkbox'+item.id} name={'checkbox'+item.id} value={'checkbox'+item.id} />
                        </CFormGroup>                      
                      </td>
                    )
                  }
              }}
            />
            <div style={{textAlign:'right'}}>{              
              ((currentPage-1)*currentPagination+1) + '-' + getLastVal(currentPage) +' of '+historyData.length
              } 
            </div>
          </CCardBody>
          </CCard>
        </CCol>      
      </CRow>
    </div>      
  )
}
