import React from "react";
import { TextField } from '@material-ui/core';
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
import InputAdornment from "@material-ui/core/InputAdornment";

// @material-ui/icons
import Face from "@material-ui/icons/Face";
import Email from "@material-ui/icons/Email";
import LockOutlined from "@material-ui/icons/LockOutlined";
import CIcon from '@coreui/icons-react'

import { NavLink as RouterLink, Route,Link, useHistory } from "react-router-dom";
import Amplify, { Auth } from 'aws-amplify';

import {
  CButton,
  CCard,
  CCardBody,
  CCardGroup,
  CCol,
  CContainer,
  CForm,
  CInput,
  CInputGroup,
  CInputGroupPrepend,
  CInputGroupText,
  CRow
} from '@coreui/react'


import styles from "../../../assets/material/jss/material-dashboard-pro-react/views/loginPageStyle.js";

const useStyles = makeStyles(styles);

export default function LoginPage() {
  const [cardAnimaton, setCardAnimation] = React.useState("cardHidden");
  setTimeout(function() {
    setCardAnimation("");
  }, 700);
  const classes = useStyles();

  const history = useHistory();
  const [email, setEmail] = React.useState('');
  const [password, setPassword] = React.useState('');


  React.useEffect(() => {
    setConfig();
  }, []);

  function setConfig() {
    Amplify.configure({
      Auth: {
        identityPoolId: process.env.REACT_APP_AWS_REGION,
        identityPoolRegion: process.env.REACT_APP_AWS_REGION,
        region: process.env.REACT_APP_AWS_REGION,
        userPoolId: process.env.REACT_APP_AWS_USER_POOL_ID,
        userPoolWebClientId: process.env.REACT_APP_AWS_USER_POOL_CLIENT_ID
      },
    });

    const ga = window.gapi && window.gapi.auth2 ?
      window.gapi.auth2.getAuthInstance() :
        null;
    if (!ga) createScript();
  }

  function googleSignIn() {
    const ga = window.gapi.auth2.getAuthInstance();
    ga.signIn().then(
      googleUser => {
        getAWSCredentials(googleUser);
    },
    error => {
      console.log(error);
    });
  }

  async function getAWSCredentials(googleUser) {
    const { id_token, expires_at } = googleUser.getAuthResponse();
    const profile = googleUser.getBasicProfile();
    let user = {
      email: profile.getEmail(),
      name: profile.getName()
    };

    const credentials = await Auth.federatedSignIn(
      'google',
      { token: id_token, expires_at },
        user
    );
    console.log('credentials', credentials);
    history.push('/modules');
  }

  function createScript() {
    // load the Google SDK
    const script = document.createElement('script');
    script.src = 'https://apis.google.com/js/platform.js';
    script.async = true;
    script.onload = initGapi;
    document.body.appendChild(script);
  }

  function initGapi() {
    // init the Google SDK client
    const g = window.gapi;
    g.load('auth2', function() {
      g.auth2.init({
        client_id: process.env.REACT_APP_AWS_GOOGLE_CLIENT_ID,
        // authorized scopes
        scope: 'profile email openid'
      });
    });
  }

  async function handleSubmit(event) {
    event.preventDefault();
    console.log(email + ' ' + password)
    try {
      const user = await Auth.signIn({
        username: email,
        password: password
      });
      console.log(user);
      history.push('/modules');
      setEmail('');
      setPassword('');
    } catch (error) {
      console.log('error signing up:', error);
    }
  }


  return (
    <div className={classes.container}>
      <CContainer>
        <CRow className="justify-content-center">
          <CCol md="4">
            <CCardGroup>
              <CCard className="p-4">
                <CCardBody>
                  <CForm id="signInForm" onSubmit={handleSubmit}>
                      <h1 style={{color:"#3c4b64"}}>Login</h1>
                      <p className="text-muted">Sign In to your account</p>
                      <CInputGroup className="mb-3">
                        <CInputGroupPrepend>
                          <CInputGroupText>
                            <CIcon name="cil-envelope-closed" />
                          </CInputGroupText>
                        </CInputGroupPrepend>
                        <CInput type="text" 
                          placeholder="Email" 
                          autoComplete="email" 
                          type = "email"
                          onChange={e => setEmail(e.target.value)}
                          value={email} 
                          required/>
                      </CInputGroup>
                      <CInputGroup className="mb-4">
                        <CInputGroupPrepend>
                          <CInputGroupText>
                            <CIcon name="cil-lock-locked" />
                          </CInputGroupText>
                        </CInputGroupPrepend>
                        <CInput 
                          type="password" 
                          placeholder="Password" 
                          autoComplete="current-password" 
                          onChange={e => setPassword(e.target.value)}
                          required
                          value={password}
                          />
                      </CInputGroup>
                      <CRow>
                        <CCol xs="5">
                          <CButton type="submit" color="primary" className="px-4">Login</CButton>
                        </CCol>
                        <CCol xs="7" className="text-right">
                          <CButton color="danger" className="px-2" onClick={googleSignIn}>Login with Google</CButton>
                        </CCol>
                      </CRow>
                    </CForm>
                  </CCardBody>
                </CCard>               
              </CCardGroup>
            </CCol>
          </CRow>
        </CContainer>      
    </div>
  );
}
