import React, { Component } from 'react'
import {
  CButton,
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CCollapse,
  CRow,CListGroup, CListGroupItem, CBadge, CDataTable, CAlert,CFormGroup,CInputCheckbox,CLabel,CInput
} from '@coreui/react';
import CIcon from '@coreui/icons-react'

import { Link } from 'react-router-dom';

import usersData from './UsersData.js'

import { makeStyles } from '@material-ui/core/styles';
import clsx from 'clsx';
import IconButton from '@material-ui/core/IconButton';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

const useStyles = makeStyles((theme) => ({
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
}));


export default function DeviceDashboard() {
  const { useState } = React;
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(true);
  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  const [currentPage, setCurrentPage] = useState(1)
  const [currentPagination, setCurrentPagination] = useState(5)
  const getLastVal=(currentPage)=>{
    if(currentPage*currentPagination < usersData.length)
      return currentPage*currentPagination;
    return usersData.length;
  }

  const [details, setDetails] = useState([])
  const toggleDetails = (index) => {
    const position = details.indexOf(index)
    let newDetails = details.slice()
    if (position !== -1) {
      newDetails.splice(position, 1)
    } else {
      newDetails = [...details, index]
    } 
    setDetails(newDetails)
  }

  const fields = [
    {
      key: 'check_device',
      label: '',
      _style: { width: '1%' },
      sorter: false,
      filter: false
    },
    { key: 'IMEI', _style: { width: '30%'} },        
    { key: 'DeviceType', _style: { width: '30%'} },
    { key: 'Status', _style: { width: '20%'} },
    { key: 'HeartBeats', _style: { width: '20%'} },
  ]

  const getBadge = (status)=>{
    switch (status) {
      case 'Online': return 'success'
      case 'Offline': return 'secondary'
      default: return 'secondary'
    }
  }

  return (
    <div className="animated fadeIn">
      <CRow> 
        <CCol xl="9">
          <CCard custom>
            <CCardHeader>
              Monitor
            </CCardHeader>
            <CCardBody>         
            <CDataTable
              items={usersData}
              fields={fields}
              // columnFilter
              tableFilter
              // footer
              itemsPerPageSelect
              itemsPerPage={currentPagination}
              dark={false}
              hover={false}
              border={true}
              outlined={true}
              loading={false}
              sorter
              // clickableRows
              pagination={{ doubleArrows: true, align: 'center' }}
              // loading
              // onRowClick={(item,index,col,e) => console.log(item,index,col,e)}
              onPageChange={(val) => {
                  console.log('new page:', val);
                  setCurrentPage(val)
                }
              }
              onPagesChange={(val) => 
                console.log('new pages:', val)
              }
              onPaginationChange={(val) => {
                console.log('new pagination:', val);
                setCurrentPagination(val);
              }}
              // onFilteredItemsChange={(val) => console.log('new filtered items:', val)}
              // onSorterValueChange={(val) => console.log('new sorter value:', val)}
              // onTableFilterChange={(val) => console.log('new table filter:', val)}
              // onColumnFilterChange={(val) => console.log('new column filter:', val)}
              scopedSlots = {{
                'IMEI':
                  (item)=>(
                    <td>
                        <Link to={'/modules/devicemanagement/dashboard/history'}>{item.IMEI}</Link>
                    </td>
                  ),
                'Status':
                  (item)=>(
                    <td>
                      <CBadge color={getBadge(item.Status)}>
                        {item.Status}
                      </CBadge>
                    </td>
                  ),
                'check_device':
                  item => {
                    return (
                      <td className="py-2">
                        <CFormGroup variant="checkbox" className="checkbox">
                          <CInputCheckbox id={'checkbox'+item.id} name={'checkbox'+item.id} value={'checkbox'+item.id} />
                        </CFormGroup>                      
                      </td>
                    )
                  }
              }}
            />
            <div style={{textAlign:'right'}}>{              
              ((currentPage-1)*currentPagination+1) + '-' + getLastVal(currentPage) +' of '+usersData.length
              } 
            </div>
          </CCardBody>
          </CCard>
        </CCol>
        <CCol xl="3">
          {/* <div id="accordion"> */}
            <CCard custom>
              <CCardHeader id="headingOne" style={{ display: 'inline-flex'}}> 
              {/* alignSelf: 'center', */}
                  <CButton block color="link" className="text-left m-0 p-0" onClick={() => handleExpandClick()}>
                    <h5 className="m-0 p-0">Commands</h5>
                  </CButton>
                  <IconButton
                    className={clsx(classes.expand, {
                      [classes.expandOpen]: expanded,
                    })}
                    onClick={handleExpandClick}
                    aria-expanded={expanded}
                    aria-label="show more"
                    style={{width:24, height:24, maringLeft:0, padding:0}}>
                    <ExpandMoreIcon style={{maring:0, padding:0}}/>
                  </IconButton> 
              </CCardHeader>
              <CCollapse custom show={expanded}>
                  <CListGroup>
                    <CListGroupItem style={{paddingRight:0, cursor:"pointer", display: 'flex'}}>
                      <CIcon name="cil-send" style={{marginRight:5}} />
                      Capture Photo
                    </CListGroupItem>
                    <CListGroupItem style={{paddingRight:0, cursor:"pointer",display: 'flex'}}>
                      <CIcon name="cil-send" style={{marginRight:5, marginBottom:2}} />
                      Capture Video
                      <CInput placeholder="mins"  style={{marginLeft:10, width:70,height:25}}/>                        
                    </CListGroupItem> 
                    <CListGroupItem style={{paddingRight:0, cursor:"pointer", display: 'flex'}}>
                      <CIcon name="cil-send" style={{marginRight:5, marginBottom:2}} />
                      Get Device Status
                    </CListGroupItem>
                    <CListGroupItem style={{paddingRight:0, cursor:"pointer",display: 'flex'}}>
                      <CIcon name="cil-send" style={{marginRight:5, marginBottom:2}} />
                      Set Heartbeats
                      <CInput placeholder="mins"  style={{marginLeft:10, width:70,height:25}}/>                        
                    </CListGroupItem>
                  </CListGroup>
              </CCollapse>
            </CCard>                 
          {/* </div>             */}
        </CCol>
      </CRow>
    </div>      
  )
}
