import React, { Component } from 'react'
import {
  CButton,
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CRow, CDataTable, CForm, CFormGroup,CFormText,CLink,CInput,CLabel,CSelect,
  CCardFooter,CInvalidFeedback
} from '@coreui/react';
import CIcon from '@coreui/icons-react'
import { Formik } from 'formik'
import * as Yup from 'yup'

import devicesData from './DevicesData.js'

const handleSubmit=(event)=>
{
  event.preventDefault();
  alert(JSON.stringify(document.getElementById('imei-input').value, null, 2))
  // setTimeout(() => {
  //    alert(JSON.stringify(document.getElementById('imei-input').value, null, 2))
  //   // console.log('User has been successfully saved!', values)
  //   // setSubmitting(false)
  // }, 2000)
}

const handleReset=()=>
{
  document.getElementById('imei-input').value = ''
  document.getElementById('imei-input').disable = true
}


export default function Devices() {
  const { useState } = React;

  const [currentPage, setCurrentPage] = useState(1)
  const [currentPagination, setCurrentPagination] = useState(5)
  const getLastVal=(currentPage)=>{
    if(currentPage*currentPagination < devicesData.length)
      return currentPage*currentPagination;
    return devicesData.length;
  }
  const fields = [    
    { key: 'IMEI', _style: { width: '30%'} },        
    { key: 'DeviceType', _style: { width: '30%'} },
    {
      key: 'actions',
      label: '',
      _style: { width: '1%' },
      sorter: false,
      filter: false
    },
  ]
  
  const removeDevice=(item)=>{
    const device = devicesData.find( device => device.id === item.id)
    //console.log(item.id)
    //console.log(device)    
    var index = devicesData.indexOf(device)
    if (index !== -1) {
      devicesData.splice(index, 1);
    }
    console.log(devicesData);
  }

  return (    
    <div className="animated fadeIn">
      <CRow> 
        <CCol xl="7">
          <CCard custom>
            <CCardHeader >  
              Device List
              {/* <CFormGroup style={{padding:0, margin:0}}  row>            
                <CCol md="4" style={{alignSelf:'center'}}>
                  Device List
                </CCol>
                <CCol xs="12" md="8" style={{textAlign:'right'}}>
                  <CButton  variant="outline" size="sm" color="primary"  onClick={()=> document.getElementById('imei-input').value='' } ><CIcon name="cil-plus" />Add Device</CButton>
                </CCol>
              </CFormGroup>               */}
            </CCardHeader>
            <CCardBody>         
            <CDataTable
              items={devicesData}
              fields={fields}
              // columnFilter
              tableFilter
              // footer
              itemsPerPageSelect
              itemsPerPage={currentPagination}
              dark={false}
              hover={false}
              border={true}
              outlined={true}
              loading={false}
              sorter
              clickableRows
              pagination={{ doubleArrows: true, align: 'center' }}
              // loading
              onRowClick={(item,index,col,e) => 
                {
                console.log(item,index,col,e)
                document.getElementById('imei-input').value = item.IMEI
                }
              }
              onPageChange={(val) => {
                  console.log('new page:', val);
                  setCurrentPage(val)
                }
              }
              onPagesChange={(val) => 
                console.log('new pages:', val)
              }
              onPaginationChange={(val) => {
                console.log('new pagination:', val);
                setCurrentPagination(val);
              }}
              // onFilteredItemsChange={(val) => console.log('new filtered items:', val)}
              // onSorterValueChange={(val) => console.log('new sorter value:', val)}
              // onTableFilterChange={(val) => console.log('new table filter:', val)}
              // onColumnFilterChange={(val) => console.log('new column filter:', val)}
              scopedSlots = {{
                'IMEI':
                  (item)=>(
                    <td>
                        <CLink to={`/modules/devicemanagement/devices/${item.id}`}>{item.IMEI}</CLink>
                    </td>
                  ),
                'actions':
                  item => {
                    return (
                      <td className="py-2">
                        <CButton color="light" onClick={()=>removeDevice(item)}><CIcon name="cil-trash" /></CButton>                      
                      </td>
                    )
                  }
              }}
            />
            <div style={{textAlign:'right'}}>{              
              ((currentPage-1)*currentPagination+1) + '-' + getLastVal(currentPage) +' of '+devicesData.length
              } 
            </div>
          </CCardBody>
          </CCard>
        </CCol>
        <CCol xl="5">
            <CCard custom>
              <CCardHeader id="headingOne" style={{ display: 'inline-flex'}}> 
                Add Device                
              </CCardHeader>                                      
                <CCardBody>                                       
                  <CForm  onSubmit={handleSubmit} className="form-horizontal"   name='simpleForm'>
                  {/* onSubmit={handleSubmit} */}
                    <CFormGroup row>
                      <CCol md="4">
                        <CLabel htmlFor="imei-input">IMEI</CLabel>
                      </CCol>
                      <CCol xs="12" md="8">
                        <CInput type="text"
                            name="imei-input"
                            id="imei-input"
                            placeholder="Please input imei"
                            autoComplete="given-name"
                            required/>
                      </CCol>
                    </CFormGroup>
                    <CFormGroup row>
                      <CCol md="4">
                        <CLabel htmlFor="select">Device Type</CLabel>
                      </CCol>
                      <CCol xs="12" md="8">
                        <CSelect custom name="select" id="select">
                          <option value="0">Raspberry PI</option>
                        </CSelect>
                      </CCol>
                    </CFormGroup>                
                    <hr/>            
                    <CButton type="submit"  variant="outline" className="mr-1" color="primary" block><CIcon name="cil-save" /> Save</CButton>         
                    <CButton type="reset" variant="outline"  className="mr-1" color="danger" onClick={handleReset} block><CIcon name="cil-ban" /> Reset</CButton>
                  </CForm>        
              </CCardBody>   
            </CCard>                 
        </CCol>
      </CRow>
    </div>      
  )
}
