import React from 'react';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardContent from '@material-ui/core/CardContent';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import Link from '@material-ui/core/Link';
import { NavLink as RouterLink, Route } from "react-router-dom";
import Navigation from '../components/Navigation';
import Paper from '@material-ui/core/Paper';
import Box from '@material-ui/core/Box';
import Copyright from '../components/Copyright';
import { useStyles } from '../theme/PageTheme';
import SendIcon from '@material-ui/icons/Send';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import axios from 'axios';
import { useTheme, makeStyles } from '@material-ui/core/styles';
import FacebookIcon from '@material-ui/icons/Facebook';
import LinkedInIcon from '@material-ui/icons/LinkedIn';
import TwitterIcon from '@material-ui/icons/Twitter';
import YouTubeIcon from '@material-ui/icons/YouTube';

export default function Contact() {
  const theme = useTheme();
  const classes = useStyles();
  const [name, setName] = React.useState('');
  function handleSubmit(event) {
    event.preventDefault();
    axios({
      url: 'https://hfa3ovai45.execute-api.us-east-2.amazonaws.com/api/category',
      method: 'post',
      data: {
        'name': name
      },
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(response => response.data).then((data) => {
      //setToastClass('success');
      //setToastMessage('Success: ' + data);
      //showToast(true);
      //setCategoryName('');
      //setParent(0);
    });
  }
  return (
    <div className={classes.root}>
      <Navigation />
      <main>
        <Container className={classes.mainContent}>
          <Grid container spacing={2} justify="center">
            <Box my={2}>
              <Typography variant="h5" color="primary">
                Contact Us
              </Typography>
            </Box>
          </Grid>
          <Grid container spacing={2}>
            <Grid item xs={12} md={7}>
              <Paper>
                <Box p={3}>
                  <Typography style={{textAlign:"center",marginBottom:theme.spacing(3)}}>
                    Drop a message, and we'll get back to you soon.
                  </Typography>
                  <form className={classes.root} onSubmit={handleSubmit}>
                    <Grid container spacing={2}>
                      <Grid item xs={12} md={6}>
                        <TextField
                          label="Name"
                          id="name"
                          variant="outlined"
                          size="small"
                          onChange={e => setName(e.target.value)}
                          required
                          value={name}
                          fullWidth
                        />
                      </Grid>
                      <Grid item xs={12} md={6}>
                        <TextField
                          label="EMail"
                          id="email"
                          variant="outlined"
                          size="small"
                          onChange={e => setName(e.target.value)}
                          required
                          value={name}
                          fullWidth
                        />
                      </Grid>
                      <Grid item xs={12}>
                        <TextField
                          label="Subject"
                          id="subject"
                          variant="outlined"
                          size="small"
                          onChange={e => setName(e.target.value)}
                          required
                          value={name}
                          fullWidth
                        />
                      </Grid>
                      <Grid item xs={12}>
                        <TextField
                          label="Message"
                          id="message"
                          variant="outlined"
                          size="small"
                          onChange={e => setName(e.target.value)}
                          required
                          value={name}
                          fullWidth
                          multiline
                          rows={4}
                        />
                      </Grid>
                    </Grid>
                    <Button
                      type="submit"
                      variant="contained"
                      color="primary"
                      style={{marginTop: theme.spacing(2)}}>
                      <SendIcon fontSize="small" /> &nbsp; Send
                    </Button>
                  </form>
                </Box>
              </Paper>
            </Grid>
            <Grid item xs={12} md={5}>
              <Paper>
                <Box p={3}>
                  <Typography style={{textAlign:"center",marginBottom:theme.spacing(3)}}>
                    Connect with Us
                  </Typography>
                  <Box style={{textAlign:"center"}}>
                    <FacebookIcon fontSize="large" color="primary" className={classes.socialIcon} />
                    <YouTubeIcon fontSize="large" color="secondary" className={classes.socialIcon} />
                    <TwitterIcon fontSize="large" style={{color:"#42a5f5"}} className={classes.socialIcon} />
                    <LinkedInIcon fontSize="large" color="primary" className={classes.socialIcon} />
                  </Box>
                </Box>
              </Paper>
            </Grid>
          </Grid>
        </Container>
      </main>
      <footer className={classes.footer}>
        <Copyright />
      </footer>
    </div>
  );
}
