            })
            .then(function (response) {
                success(response.data);
            })
            .catch(function (error) {
                console.log(error.response)
                fail(error.response);
            });
    },
  
    deleteCategory(id, success, fail) {
        console.log(id)

        axios({
        url: Config.BACKEND_API_URL+'/category',
        method: 'delete',
            data: {
                'categoryId': id
            }
            ,
            headers: {
                'Content-Type': 'application/json'
            }
        }).then(function (response) {
            success(response.data);
        })
        .catch(function (error) {
            console.log(error.response)
            fail(error.response.data);
        });

        // axios.delete(Config.BACKEND_API_URL+'/category',
        //     {                  
        //       categoryId: id
        //     })
        //     .then(function (response) {
        //         success(response.data);
        //     })
        //     .catch(function (error) {
        //         console.log(error.response)
        //         fail(error.response.data);
        //     });
    },
    // createUser creates user with data
    createCategory(category, success, fail) {
        api.shared().post('/category',
            {
                name: category.name,
                parent: category.parent
            })
            .then(function (response) {
                success(response.data);
            })
            .catch(function (error) {
                console.log(error.response)
                fail(error.response.data);
            });
    }
}

export default CategoryService;
