import axios from 'axios'
import api from './api'
import Config from '../Common/Config'

var CategoryService = {

    getCategoryList(query, success, fail) {
        api.shared().get('/category',
            {

            })
            .then(function (response) {
                success(response.data);
            })
            .catch(function (error) {
                console.log(error.response)
                fail(error.response);
            });
    },
  
    deleteCategory(id, success, fail) {
        console.log(id)
        axios.delete(Config.BACKEND_API_URL+'/category',
            {   
                data:{
                categoryId: id
                },
                headers: {
                    'Content-Type': 'application/json'
                }
            })
            .then(function (response) {
                success(response.data);
            })
            .catch(function (error) {
                console.log(error.response)
                fail(error.response.data);
            });
    },
    // createUser creates user with data
    createCategory(category, success, fail) {
        api.shared().post('/category',
            {
                name: category.name,
                parent: category.parent
            })
            .then(function (response) {
                success(response.data);
            })
            .catch(function (error) {
                console.log(error.response)
                fail(error.response.data);
            });
    }
}

export default CategoryService;
