import React from "react";

import {
  CBadge,
  CCard,
  CCardBody,
  CCardFooter,
  CCardHeader,
  CCol,
  CRow,
  CContainer,
  CButton
} from  '@coreui/react'

import CIcon from '@coreui/icons-react'

import StripeCheckout from 'react-stripe-checkout';

// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
import axios from 'axios';

// import  { useStyles }  from '../../Layout/Theme/PageTheme';
import styles from "../../../assets/material/jss/material-dashboard-pro-react/views/pricingPageStyle.js";

const useStyles = makeStyles(styles);
export default function HomePage() {

  const classes = useStyles();
  const [course1, setCourse1] = React.useState({})

  const [courseList, setCourseList] = React.useState([]);

  React.useEffect(() => {
    getCourseList();
  }, []);

  const getCourseList = async () => {
    const dataList = await axios({
      url: process.env.REACT_APP_AWS_API_ENDPOINT + '/course',
      method: 'get'
    }).then(response => response.data).then((data) => {
      return data;
    });
    if(dataList) { setCourseList(dataList); }
  };

  function addPayment(amount, currency,token) {       
    axios({
      url: process.env.REACT_APP_AWS_API_ENDPOINT + '/payment',
      method: 'post',
      data: {
        // 'title': courseTitle,
        // 'description': courseDescription,
        // 'category': JSON.stringify(category),
        // 'level': JSON.stringify(level),
        // 'imageFile': imageFile,
        // 'videoFile': videoFile
      },
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(response => response.data).then((data) => {

    }).catch(error => { 
      if (error.response) {
        console.log(error.response.data);
        console.log(error.response.status);
        console.log(error.response.headers);
      } else if (error.request) {
          console.log(error.request);
      } else {
          console.log('Error', error.message);
      }
      console.log(error.config);
    })     
  }

  function onToken1(token) 
  {
    console.log('token:'+JSON.stringify(token))
    addPayment(8000,'SGD', token)
  }

  function onToken2(token) 
  {
    console.log('token:'+JSON.stringify(token))
    addPayment(10000,'SGD', token)    
  }


  return (
    <div className={classes.container}>
      <CContainer style={{marginBottom:20}}>
        <CRow className="justify-content-center">
          <h4 >Get a chance to win lifetime education for Free</h4>
        </CRow>
        <CRow className="justify-content-center">
          <h4 >No lock-ins. Cancel anytime! We're mission - driven, not monday - driven.</h4>
        </CRow>
      </CContainer>
