                  <CRow className="justify-content-center" style={{color:'black'}} >
                    {/* <CCol xs="12" sm="8" md="6" xl="4" style={{padding:0}}>                     */}
                      <div style={{borderWidth:'1px',borderStyle:"solid",borderColor:"#ced2d8",borderRadius:"0.6rem",width:'5.5rem',height:'5.5rem'}}>
                        <CRow className="justify-content-center" style={{height:'3rem', alignItems:'flex-end'}}><CIcon name="cib-skype" style={{width:"2.5rem",height:"2.5rem"}} /></CRow>
                        <CRow className="justify-content-center" style={{height:'2.5rem',alignItems:'center'}}><span  style={{color:"#454545", fontSize:'2.5mm', width:"5rem"}}>Introduction to Coding</span></CRow>
                      </div>
                    {/* </CCol> */}
                  </CRow>
                </CContainer>

                <CContainer style={{textAlign:'center',marginBottom:20}}>
                  <CRow className="justify-content-center" >
                    <span className="h2" style={{color:"#fe9953"}}>US$249</span>                        
                  </CRow>
                  <span className="h6" style={{color:"#000000", background:"#fde4d0", padding:5}}>Price Per Class US$ 31</span>
                </CContainer>

                <CContainer style={{textAlign:'center'}}>
                  {/* <CRow className="justify-content-center" >                     */}
                    {/* <CButton size="lg" block style={{color:"#ffffff",background:"#fe9953", marginLeft:30,marginRight:30}}  to={`/auth/payment/0`}>BOOK NOW</CButton>                       */}
                    <StripeCheckout
                      name="Introduction to IOT"
                      description="8 Classes"
                      token={onToken1}
                      stripeKey="pk_test_51HoJ7lEW8ELLg74EpUwGfDBHRE818ndk53WS4MP1OfHKGcUhppaQjdrxZJ0SHQ33hxJAi7sPNIJkhqoPJF6940Fr00txoaVRxn"
                      amount={8000} // cents
                      currency="SGD"
                      image="https://stripe.com/img/documentation/checkout/marketplace.png"
                      zipCode={false}
                      allowRememberMe={false}
                    >
                      <CButton size="lg" block style={{color:"#ffffff",background:"#fe9953", marginLeft:0,marginRight:0}}>BOOK NOW</CButton>
                    </StripeCheckout>
                  {/* </CRow> */}
                  <span  style={{color:"#000000", fontSize:'2.8mm'}}>Cancel Anytime. Same day 100% refund. No questions asked</span>
                </CContainer>
              </CCardBody>
            </CCard>
          </CCol>

          <CCol xs="12" sm="12" md="4" >
            <CCard style={{borderRadius:'1rem', marginLeft:10,marginRight:10}}>
              <CCardHeader style={{borderTopLeftRadius:"1rem",borderTopRightRadius:"1rem",background:"#fff8f5"}}>   
                <CRow className="justify-content-center" style={{textAlign:'center', marginTop:20}}>    
                  <span className="h5" style={{color:"#000000"}}>IOT Intermediate Level</span>   
                </CRow>
                <CRow className="justify-content-center" style={{textAlign:'center'}}>
                  <span className="h6" style={{color:"#000000"}}>48 Classes</span>
                </CRow>
              </CCardHeader>
              <CCardBody style={{paddingLeft:'3px', paddingRight:'3px'}}>
                <CContainer>
                  <CRow className="justify-content-center" style={{textAlign:'center',marginBottom:20}}>
                    <table>
                      <tr>
                        <td><span className="h6" style={{color:"#000000"}}>Curriculum</span></td>
                      </tr>
                      <tr>
                        <td><span  style={{color:"#000000",fontSize:'3mm'}}>Animations, Game Design, Advanced App Structures</span></td>
                      </tr>
                    </table>
                  </CRow>
                </CContainer>
                <CContainer >
                  <CRow className="justify-content-center"  style={{textAlign:'center',marginBottom:20}}>
                    <table>
                      <tr>
                        <td><span className="h6" style={{color:"#000000"}}>Activities</span></td>
                      </tr>
                      <tr>
                        <td><span  style={{color:"#000000", fontSize:'3mm'}}>Create Interactive Apps with Deep UX/UI Interface</span></td>
                      </tr>
                    </table>
                  </CRow>
                </CContainer>
                <CContainer>
                  <CRow className="justify-content-center" style={{textAlign:'center',marginBottom:20}}>
                    <table>
                      <tr>
                        <td><span className="h6" style={{color:"#000000"}}>Cognitive Benefits</span></td>
                      </tr>
                      <tr>
                        <td><span  style={{color:"#000000", fontSize:'3mm'}}>Full Enterpreneur Creative Expression.</span></td>
                      </tr>
                    </table>
                  </CRow>
                </CContainer>
                <CContainer style={{textAlign:'center',marginBottom:30}}>
                  <CRow className="justify-content-center" >
                    <span className="h6" style={{color:"#000000"}}>Achievements</span>                        
                  </CRow>
                  <CRow className="justify-content-center" style={{color:'black'}} >
                    {/* <CCol xs="12" sm="8" md="6" xl="4" style={{padding:0}}>                     */}
                      <div style={{borderWidth:'1px',borderStyle:"solid",borderColor:"#ced2d8",borderRadius:"0.6rem",width:'5.5rem',height:'5.5rem', marginLeft:3,marginRight:3}}>
                        <CRow className="justify-content-center" style={{height:'3rem', alignItems:'flex-end'}}><CIcon name="cib-skype" style={{width:"2.5rem",height:"2.5rem"}} /></CRow>
                        <CRow className="justify-content-center" style={{height:'2.5rem',alignItems:'center'}}><span  style={{color:"#454545", fontSize:'2.5mm', width:"5rem"}}>Certificate</span></CRow>
                      </div>
                    {/* </CCol> */}
                    {/* <CCol xs="12" sm="8" md="6" xl="4" style={{padding:0}}>                     */}
                      <div style={{borderWidth:'1px',borderStyle:"solid",borderColor:"#ced2d8",borderRadius:"0.6rem",width:'5.5rem',height:'5.5rem',marginLeft:3,marginRight:3}}>
                        <CRow className="justify-content-center" style={{height:'3rem', alignItems:'flex-end'}}><CIcon name="cib-skype" style={{width:"2.5rem",height:"2.5rem"}}/></CRow>
                        <CRow className="justify-content-center" style={{height:'2.5rem',alignItems:'center'}}><span  style={{color:"#454545", fontSize:'2.5mm', width:"5rem"}}>Interactive Game Design</span></CRow>
