import React, { Component, useEffect } from 'react'
import {
  CButton,
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CRow, CDataTable, CForm, CFormGroup, CFormText, CLink, CInput, CLabel, CSelect,
} from '@coreui/react';

import MuiAlert from '@material-ui/lab/Alert';
import Snackbar from '@material-ui/core/Snackbar';

import CIcon from '@coreui/icons-react'
import CategoryService from '../../API/CategoryService';
import CourseService from '../../API/CourseService';

function Alert(props) {
  return <MuiAlert elevation={6} {...props} />;
}

export default function Courses() {
  const { useState } = React;
  const [currentPage, setCurrentPage] = useState(1)
  const [currentPagination, setCurrentPagination] = useState(5)
  const getLastVal = (currentPage) => {
    if (currentPage * currentPagination < categoryList.length)
      return currentPage * currentPagination;
    return categoryList.length;
  }
  const fields = [
    { key: 'Title', _style: { width: '30%' } },
    {
      key: 'actions',
      label: '',
      _style: { width: '1%' },
      sorter: false,
      filter: false
    },
  ]

  const [value, setValue] = React.useState(0);
  const [categoryList, setCategoryList] = React.useState([]);
  const [courseList, setCourseList] = React.useState([]);
  const [levelList, setLevelList] = React.useState([]);
  const [courseTitle, setCourseTitle] = React.useState('');
  const [courseDescription, setCourseDescription] = React.useState('');
  const [level, setLevel] = React.useState(0);
  const [category, setCategory] = React.useState(0);
  const [image, setImage] = React.useState(null);
  const [imageHeight, setImageHeight] = React.useState('0px');
  const [toast, showToast] = React.useState(false);
  const [toastMessage, setToastMessage] = React.useState('');
  const [toastClass, setToastClass] = React.useState('success');
  const [loading, setLoading] = React.useState(false);
  const [imageFile, setImageFile] = React.useState('');

  React.useEffect(() => {
    getCategoryList();
    setLevelList([{levelId:1, name:'Beginner'},{levelId:2, name:'Intermediate'},{levelId:3, name:'Expert'}]);
  }, []);

  const getCategoryList = async () => {
    setLoading(true)
    // get categories from backend service via service api
    await CategoryService.getCategoryList('', (res) => {      
      console.log(res)     
      setCategoryList(res); 
      setLoading(false)
    }, (err) => {
      setLoading(false)
    });
  };
  
  // const [isAddDevice, setIsAddDevice] = useState(true)
  // const [editId, setEditId] = useState(-1)

  const deleteCategory = (item) => {
    // console.log(item)
    setIsLoading(true)
    CategoryService.deleteCategory(item.categoryId, (res) => {
      setToastClass('success');
      setToastMessage('Success: ' + res);
      showToast(true);
      setIsLoading(false)
    }, (err) => {
      setIsLoading(false)
    });
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    setIsLoading(true)
    CategoryService.createCategory(
      {
        name: categoryName,
        parent: parent
      }, (res) => {
        setToastClass('success');
        setToastMessage('Success: ' + res);
        showToast(true);

        setCategoryName('');
        setParent(0);
        setIsLoading(false)
      }, (err) => {
        setIsLoading(false)
      });
  }


  return (
    <div className="animated fadeIn">
      <CRow>
        <CCol xl="6">
          <CCard custom>
            <CCardHeader >
              Courses
            </CCardHeader>
            <CCardBody>
              <CDataTable
                items={categoryList}
                fields={fields}
                // columnFilter
                tableFilter
                // footer
                itemsPerPageSelect
                itemsPerPage={currentPagination}
                dark={false}
                hover={false}
                border={true}
                outlined={true}
                loading={false}
                sorter
                // clickableRows
                pagination={{ doubleArrows: true, align: 'center' }}
                loading={isLoading}
                // onRowClick={(item,index,col,e) => {console.log(item,index,col,e)}}
                onPageChange={(val) => {
                  console.log('new page:', val);
                  setCurrentPage(val)
                }
                }
                // onPagesChange={(val) => console.log('new pages:', val)}
                onPaginationChange={(val) => {
                  console.log('new pagination:', val);
                  setCurrentPagination(val);
                }}
                // onFilteredItemsChange={(val) => console.log('new filtered items:', val)}
                // onSorterValueChange={(val) => console.log('new sorter value:', val)}
                // onTableFilterChange={(val) => console.log('new table filter:', val)}
                // onColumnFilterChange={(val) => console.log('new column filter:', val)}
                scopedSlots={{
                  'parentName':
                  (item) => (
                    <td>
                      { item.parent !== 0 &&
                          categoryList.find(pCat => pCat.categoryId === item.parent).name
                      }                      
                    </td>
                  ),
                  'actions':
                    item => {
                      return (
                        <td className="py-2">
                          <CButton color="light"  onClick={(e) => { e.preventDefault(); deleteCategory(item) }}><CIcon name="cil-trash" /></CButton>
                        </td>
                      )
                    }
                }}
              />
              <div style={{ textAlign: 'right' }}>{
                ((currentPage - 1) * currentPagination + 1) + '-' + getLastVal(currentPage) + ' of ' + categoryList.length
              }
              </div>
            </CCardBody>
          </CCard>
        </CCol>
        <CCol xl="6">
          <CCard custom>
            <CCardHeader id="headingOne" style={{ display: 'inline-flex' }}>
              {
                'Add Course'
              }
            </CCardHeader>
            <CCardBody>
              <CForm onSubmit={handleSubmit} className="form-horizontal" name='simpleForm'>
                {/* onSubmit={handleSubmit} */}
                <CFormGroup row>
                  <CCol md="4">
                    <CLabel htmlFor="categoryname-input">Category Name</CLabel>
                  </CCol>
                  <CCol xs="12" md="8">
                    <CInput type="text"
                      label="Category Name"
                      name="categoryname-input"
                      id="categoryname-input"
                      placeholder="Please input category name"
                      autoComplete="given-name"
                      onChange={e => setCategoryName(e.target.value)}
                      value={categoryName}
                      required />
                  </CCol>
                </CFormGroup>
                <CFormGroup row>
                  <CCol md="4">
                    <CLabel htmlFor="parent">Parent Category</CLabel>
                  </CCol>
                  <CCol xs="12" md="8">
                    <CSelect 
                      custom name="parent" 
                      label="Parent Category"
                      id="parent"
                      onChange={e => setParent(e.target.value)}
                      value={parent}
                    >
                    <option value={0}>None</option>
                    {categoryList.map((category) => (
                      <option value={category.categoryId}>{category.name}</option>
                    ))}
                    </CSelect>
                  </CCol>
                </CFormGroup>
                <hr />
                <CButton type="submit" variant="outline" className="mr-1" color="primary" block><CIcon name="cil-save" /> Save</CButton>
                {/* <CButton type="reset" variant="outline" className="mr-1" color="danger" onClick={handleReset} block><CIcon name="cil-ban" /> Reset</CButton> */}
              </CForm>
            </CCardBody>
          </CCard>
        </CCol>
        <CCol sm="12" lg="6">
          <Snackbar
            anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
            open={toast}
            autoHideDuration={3000}
            onClose={() => { showToast(false); getCategoryList(); }}>
            <Alert
              severity={toastClass}
              action={
                <CButton  onClick={(e) => { e.preventDefault(); showToast(false); getCategoryList();  }}><CIcon name="cil-x" /></CButton>
              }
            >
              {toastMessage}
            </Alert>
          </Snackbar>
        </CCol>
      </CRow>
    </div>
  )
}
