import React from 'react';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import SendIcon from '@material-ui/icons/Send';
import { Divider } from '../../node_modules/@material-ui/core';

import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import FormControl from '@material-ui/core/FormControl';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';
import Switch from '@material-ui/core/Switch';
import MaterialTable from 'material-table'
import DeviceSensorHistory from '../components/DeviceSensorHistory'


const tableIcons  = {
  
  Add: forwardRef((props, ref) => <AddBox {...props} ref={ref} />),
  Check: forwardRef((props, ref) => <Check {...props} ref={ref} />),
  Clear: forwardRef((props, ref) => <Clear {...props} ref={ref} />),
  Delete: forwardRef((props, ref) => <DeleteOutline {...props} ref={ref} />),
  DetailPanel: forwardRef((props, ref) => <ChevronRight {...props} ref={ref} />),
  Edit: forwardRef((props, ref) => <Edit {...props} ref={ref} />),
  Export: forwardRef((props, ref) => <SaveAlt {...props} ref={ref} />),
  Filter: forwardRef((props, ref) => <FilterList {...props} ref={ref} />),
  FirstPage: forwardRef((props, ref) => <FirstPage {...props} ref={ref} />),
  LastPage: forwardRef((props, ref) => <LastPage {...props} ref={ref} />),
  NextPage: forwardRef((props, ref) => <ChevronRight {...props} ref={ref} />),
  PreviousPage: forwardRef((props, ref) => <ChevronLeft {...props} ref={ref} />),
  ResetSearch: forwardRef((props, ref) => <Clear {...props} ref={ref} />),
  Search: forwardRef((props, ref) => <Search {...props} ref={ref} />),
  SortArrow: forwardRef((props, ref) => <ArrowDownward {...props} ref={ref} />),
  ThirdStateCheck: forwardRef((props, ref) => <Remove {...props} ref={ref} />),
  ViewColumn: forwardRef((props, ref) => <ViewColumn {...props} ref={ref} />)
};

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  details: {
    display: 'flex',
    flexDirection: 'column',
  },
  content: {
    flex: '1 0 auto',
  },
  cover: {
    width: 151,
  },
  controls: {
    display: 'flex',
    alignItems: 'center',
    paddingLeft: theme.spacing(1),
    paddingBottom: theme.spacing(1),
  },
}));


export default function DeviceSensorCard(props) {
  const { useState } = React;
  const [selectedRow, setSelectedRow] = useState(null);
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);
  const [fullWidth, setFullWidth] = React.useState(true);
  const [maxWidth, setMaxWidth] = React.useState('sm');

  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  const handleMaxWidthChange = (event) => {
    setMaxWidth(event.target.value);
  };

  const handleFullWidthChange = (event) => {
    setFullWidth(event.target.checked);
  };

  const [columns, setColumns] = useState([
    { title: 'No', field: 'no', editable: 'onUpdate' },
    { title: 'IMEI', field: 'imei', editable: 'onUpdate' },
    {
      title: 'Customer',
      field: 'customer',
      lookup: { 34: 'Jane', 63: 'John' },
    },
    { title: 'Device Name', field: 'deviceName', editable: 'onUpdate' },
    { title: 'Device Status', field: 'deviceStatus', editable: 'onUpdate' },
    { title: 'HeartBeats', field: 'heartBeats', editable: 'onUpdate' },
  ]);
  
  const [data, setData] = useState([
    { no: '1', imei: '861475030047510', customer: 34, deviceName:'Raspberry PI 3', deviceStatus:'Online', heartBeats:'2hours 30mins' },
    { no: '2', imei: '863158020785889', customer: 63, deviceName:'Raspberry PI 3', deviceStatus:'Online', heartBeats:'2hours 30mins' },
    { no: '3', imei: '863158020785889', customer: 63, deviceName:'Raspberry PI 3', deviceStatus:'Offline', heartBeats:'2hours 30mins' },
    { no: '4', imei: '863158020785889', customer: 63, deviceName:'Raspberry PI 3', deviceStatus:'Offline', heartBeats:'2hours 30mins' },
    { no: '5', imei: '863158020785889', customer: 63, deviceName:'Raspberry PI 3', deviceStatus:'Online', heartBeats:'2hours 30mins' },
    { no: '6', imei: '863158020785889', customer: 63, deviceName:'Arduino UO', deviceStatus:'Online', heartBeats:'2hours 30mins' },
    { no: '7', imei: '863158020785889', customer: 63, deviceName:'Arduino UO', deviceStatus:'Offline', heartBeats:'2hours 30mins' },
    { no: '8', imei: '863158020785889', customer: 63, deviceName:'Arduino UO', deviceStatus:'Offline', heartBeats:'2hours 30mins' },
    { no: '9', imei: '863158020785889', customer: 63, deviceName:'Arduino UO', deviceStatus:'Online', heartBeats:'2hours 30mins' },
    { no: '10', imei: '863158020785889', customer: 63,deviceName:'Arduino UO', deviceStatus:'Online', heartBeats:'2hours 30mins' },
  ]);


  return (
    <React.Fragment>
    <Card style={{background:props.backcolor}}>
      <div className={classes.details}>
        <CardContent className={classes.content}>
          <Typography component="h5" variant="h5" style={{color:"#ffffff"}} >
            {props.name}
          </Typography>
          <Typography variant="subtitle1" color="textSecondary" style={{color:"#ffffff"}}>
            {props.count}
          </Typography>
        </CardContent>
        <Divider/>
        <div style={{cursor:"pointer", padding:5, background:props.morebgcolor }} className="flex-container" onClick={handleClickOpen}>
          <span style={{color:"#ffffff"}} className="row">More Info</span>
          <SendIcon style={{color:"#ffffff",width:20,marginLeft:10,marginRight:10}}/>
        </div>
      </div>
      <style>{`
              
        .flex-container {
          padding: 0;
          margin: 0;
          list-style: none;
          
          display: -webkit-box;
          display: -moz-box;
          display: -ms-flexbox;
          display: -webkit-flex;
          display: flex;
            align-items: center;
            justify-content: center;

        }

        row{
          width: 100%;
        }

        .flex-item {
          background: tomato;
          padding: 5px;
          width: 200px;
          height: 150px;
          margin: 10px;
          
          line-height: 150px;
          color: white;
          font-weight: bold;
          font-size: 3em;
          text-align: center;
        }
      `}</style>

      <Dialog
        fullWidth={fullWidth}
        maxWidth={maxWidth}
        open={open}
        onClose={handleClose}
        aria-labelledby="max-width-dialog-title">
        <DialogTitle id="max-width-dialog-title">Optional sizes</DialogTitle>
        <DialogContent>
        <MaterialTable
            title="Monitor"
            columns={columns}
            data={data}
            // icons={tableIcons}
            onRowClick={((evt, selectedRow) => setSelectedRow(selectedRow.tableData.id))}
            options={{
              headerStyle: {
                backgroundColor: '#039be5',
                color: '#FFF'
              },
              pageSize:4,
              pageSizeOptions:[4,8,12],
              // rowStyle: rowData => ({
              //   // backgroundColor: (selectedRow === rowData.tableData.id) ? '#EEE' : '#FFF'
              // })
            }}           
          />
          <DialogContentText>
            You can set my maximum width and whether to adapt or not.
          </DialogContentText>
          <form className={classes.form} noValidate>
            <FormControl className={classes.formControl}>
              <InputLabel htmlFor="max-width">maxWidth</InputLabel>
              <Select
                autoFocus
                value={maxWidth}
                onChange={handleMaxWidthChange}
                inputProps={{
                  name: 'max-width',
                  id: 'max-width',
                }}
              >
                <MenuItem value={false}>false</MenuItem>
                <MenuItem value="xs">xs</MenuItem>
                <MenuItem value="sm">sm</MenuItem>
                <MenuItem value="md">md</MenuItem>
                <MenuItem value="lg">lg</MenuItem>
                <MenuItem value="xl">xl</MenuItem>
              </Select>
            </FormControl>
            <FormControlLabel
              className={classes.formControlLabel}
              control={<Switch checked={fullWidth} onChange={handleFullWidthChange} />}
              label="Full width"
            />
          </form>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Close
          </Button>
        </DialogActions>
      </Dialog>
    </Card>
    </React.Fragment>
  );
}
