import S3 from "react-aws-s3";

import CIcon from '@coreui/icons-react'
import CategoryService from '../../API/CategoryService';
import CourseService from '../../API/CourseService';
import UploadService from '../../API/UploadService';

function Alert(props) {
  return <MuiAlert elevation={6} {...props} />;
}

export default function Courses() {
  const { useState } = React;
  const [currentPage, setCurrentPage] = useState(1)
  const [currentPagination, setCurrentPagination] = useState(5)
  const getLastVal = (currentPage) => {
    if (currentPage * currentPagination < categoryList.length)
      return currentPage * currentPagination;
    return categoryList.length;
  }
  const fields = [
    { key: 'title',label:'Title', _style: { width: '20%' } },
    {
      key: 'actions',
      label: '',
      _style: { width: '1%' },
      sorter: false,
      filter: false
    },
  ]

  const [categoryList, setCategoryList] = React.useState([]);
  const [courseList, setCourseList] = React.useState([]);
  const [levelList, setLevelList] = React.useState([]);
  const [courseTitle, setCourseTitle] = React.useState('');
  const [courseDescription, setCourseDescription] = React.useState('');
  const [level, setLevel] = React.useState(0);
  const [category, setCategory] = React.useState(0);
  const [image, setImage] = React.useState(null);
  const [video, setVideo] = React.useState(null);
  const [imageHeight, setImageHeight] = React.useState('0px');
  const [videoHeight, setVideoHeight] = React.useState('0px');
  const [toast, showToast] = React.useState(false);
  const [toastMessage, setToastMessage] = React.useState('');
  const [toastClass, setToastClass] = React.useState('success');
  const [loading, setLoading] = React.useState(false);

  const [imageLoading, setImageLoading] = React.useState(false);
  const [videoLoading, setVideoLoading] = React.useState(false);
  const [imageFile, setImageFile] = React.useState('');
  const [videoFile, setVideoFile] = React.useState('');

  React.useEffect(() => {
    getCategoryList();
    setLevelList([{levelId:1, name:'Beginner'},{levelId:2, name:'Intermediate'},{levelId:3, name:'Expert'}]);
  }, []);

  const getCategoryList = async () => {
    setLoading(true)
    // get categories from backend service via service api
    await CategoryService.getCategoryList('', (res) => {      
      console.log(res)     
      setCategoryList(res); 
      setLoading(false)
      getCourseList();            
    }, (err) => {
      setLoading(false)
    });
  };
  
  const getCourseList = async () => {
    console.log(process.env.REACT_APP_AWS_ASSETS_BUCKET)
    setLoading(true)
    // get categories from backend service via service api
    await CourseService.getCourseList('', (res) => {      
      console.log(res)     
      setCourseList(res); 
      setLoading(false)
    }, (err) => {
      setLoading(false)
    });
  };

  function deleteCourse(item) {
    setLoading(true)
    CourseService.deleteCourse(item.courseId, (res) => {
      setToastClass('success');
      setToastMessage('Success: ' + res);
      showToast(true);
      setLoading(false)
    }, (err) => {
      setLoading(false)
    });  
  }
  
  function imageUpload(file) {
    
    let fileName = Date.now().toString();
    const config = {
      bucketName: process.env.REACT_APP_AWS_ASSETS_BUCKET,
      region: process.env.REACT_APP_AWS_REGION,
      accessKeyId: process.env.REACT_APP_AWS_ACCESS_KEY,
      secretAccessKey: process.env.REACT_APP_AWS_SECRET_ACCESS_KEY
    };
    const ReactS3Client = new S3(config);
    ReactS3Client.uploadFile(file, fileName).then((data) => {
      if (data.status === 204) {
        setImageFile(data.key);
        setImageHeight("125px");
        setImageLoading(false);
      } else {
        console.log(data);
      }
    });
  };

  function setThumbnail(event) {
    event.preventDefault();
    setImageLoading(true);
    let file = event.target.files[0];
    setImage(URL.createObjectURL(file));
    imageUpload(file);
  };

  function setVideoThumbnail(event) {
    event.preventDefault();
    setVideoLoading(true);
    let file = event.target.files[0];
    setVideo(URL.createObjectURL(file));
    videoUpload(file);
  };

  function videoUpload(file) {
    let fileName = Date.now().toString();
    const config = {
      bucketName: process.env.REACT_APP_AWS_ASSETS_BUCKET,
      region: process.env.REACT_APP_AWS_REGION,
      accessKeyId: process.env.REACT_APP_AWS_ACCESS_KEY,
      secretAccessKey: process.env.REACT_APP_AWS_SECRET_ACCESS_KEY
    };
    const ReactS3Client = new S3(config);
    ReactS3Client.uploadFile(file, fileName).then((data) => {
      if (data.status === 204) {
        setVideoFile(data.key);
        setVideoHeight("125px");
        setVideoLoading(false);
      } else {
        console.log(data);
      }
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setLoading(true)
    const formData = new FormData();
    formData.append('file', image);   
    console.log(category)
    CourseService.createCourse(
      {
        title: courseTitle,
        description: courseDescription,
        category: JSON.stringify(category),
        level: JSON.stringify(level),
        imageFile: imageFile,
        videoFile: videoFile
      }, (res) => {
        setToastClass('success');
        setToastMessage('Success: ' + res);
        showToast(true);
        setCourseTitle('');
        setCourseDescription('');
        setCategory(0);
        setImage(null);
        setVideo(null);
        setImageHeight('0px');
        setVideoHeight('0px');
        setImageFile('');
        setVideoFile('');
        setLoading(false)
      }, (err) => {
        setToastClass('Warning');
        setToastMessage('Warning: ' + err);
        showToast(true);
        setLoading(false)
      });
  }

  return (
    <div className="animated fadeIn">
      <CRow>
        <CCol xl="6">
          <CCard custom>
            <CCardHeader >
              Courses
            </CCardHeader>
            <CCardBody>
              <CDataTable
                items={courseList}
                fields={fields}
                // columnFilter
                tableFilter
                // footer
                itemsPerPageSelect
                itemsPerPage={currentPagination}
                dark={false}
                hover={false}
                border={false}
                outlined={false}
                loading={false}
                sorter={false}
                // clickableRows
                pagination={{ doubleArrows: true, align: 'center' }}
                loading={loading}
                // onRowClick={(item,index,col,e) => {console.log(item,index,col,e)}}
                onPageChange={(val) => {
                  console.log('new page:', val);
                  setCurrentPage(val)
                }
                }
                // onPagesChange={(val) => console.log('new pages:', val)}
                onPaginationChange={(val) => {
                  console.log('new pagination:', val);
                  setCurrentPagination(val);
                }}
                // onFilteredItemsChange={(val) => console.log('new filtered items:', val)}
                // onSorterValueChange={(val) => console.log('new sorter value:', val)}
                // onTableFilterChange={(val) => console.log('new table filter:', val)}
                // onColumnFilterChange={(val) => console.log('new column filter:', val)}
                scopedSlots={{                 
                  'actions':
                    item => {
                      return (
                        <td className="py-2">
                          {/* <CButton color="light"  onClick={(e) => { e.preventDefault(); deleteCourse(item) }}><CIcon name="cil-trash" /></CButton> */}
                          <CButton size="sm" color="danger" className="ml-1" onClick={(e) => { e.preventDefault(); deleteCourse(item) }}>
                            Delete
                          </CButton>
                        </td>
                      )
                    }
                }}
              />
              <div style={{ textAlign: 'right' }}>{
                ((currentPage - 1) * currentPagination + 1) + '-' + getLastVal(currentPage) + ' of ' + categoryList.length
              }
              </div>
            </CCardBody>
          </CCard>
        </CCol>
        <CCol xl="6">
          <CCard custom>
            <CCardHeader id="headingOne" style={{ display: 'inline-flex' }}>
              {
                'Add Course'
              }
            </CCardHeader>
            <CCardBody>
              <CForm onSubmit={handleSubmit} className="form-horizontal" name='simpleForm'>
                <CInputGroup className="mb-3">
                  <CInputGroupPrepend>
                    <CInputGroupText>@</CInputGroupText>
                  </CInputGroupPrepend>
                  <CInput type="text"
                      label="Course Title"
                      name="coursetitle-input"
                      id="coursetitle-input"
                      placeholder="Course Title"
                      autoComplete="given-name"
                      onChange={e => setCourseTitle(e.target.value)}
                      value={courseTitle}
                      required />
                </CInputGroup>
                <CInputGroup className="mb-3">
                  <CInputGroupPrepend>
                    <CInputGroupText>@</CInputGroupText>
                  </CInputGroupPrepend>
                  <CInput type="text"
                      label="Course Description"
                      name="coursedescription-input"
                      id="coursedescription-input"
                      placeholder="Course Description"
                      autoComplete="given-name"
                      onChange={e => setCourseDescription(e.target.value)}
                      value={courseDescription}
                      required />
                </CInputGroup>
                <CInputGroup className="mb-3">
                  <CInputGroupPrepend>
                    <CInputGroupText>@</CInputGroupText>
                  </CInputGroupPrepend>
                  <CSelect 
                      custom name="category" 
                      label="Category"
                      id="category"
                      onChange={e => setCategory(e.target.value)}
                      value={category}
                    >
                    {categoryList.map((category) => (
                      <option value={category.categoryId}>{category.name}</option>
                    ))}
                  </CSelect>
                </CInputGroup>
                <CInputGroup className="mb-3">
                  <CInputGroupPrepend>
                    <CInputGroupText>@</CInputGroupText>
                  </CInputGroupPrepend>
                  <CSelect 
                      custom name="level" 
                      label="levelLabel"
                      id="level"
                      onChange={e => setLevel(e.target.value)}
                      // value={level}
                    >
                    <option value={levelList[0]}>Beginner</option>
                    <option value={levelList[1]}>Intermediate</option>
                    <option value={levelList[2]}>Expert</option>
                  </CSelect>
                </CInputGroup>

                {/* <CFormGroup row>
                  <CCol md="4">
                    <CLabel htmlFor="levelLabel">Level</CLabel>
                  </CCol>
                  <CCol xs="12" md="8">
                    <CSelect 
                      custom name="level" 
                      label="levelLabel"
                      id="level"
                      onChange={e => setLevel(e.target.value)}
                      // value={level}
                    >
                    <option value={levelList[0]}>Beginner</option>
                    <option value={levelList[1]}>Intermediate</option>
                    <option value={levelList[2]}>Expert</option>
                    </CSelect>
                  </CCol>
                </CFormGroup> */}
                <CFormGroup row>
                  <CCol  md={6}>                    
                    {/* <Button variant="contained" component="label"> */}
                    <Button size="sm" color="primary" variant="contained" >
                      <PhotoLibraryIcon /> &nbsp; Upload Image
                      <input type="file" accept="image/*" style={{display:"none"}} onChange={setThumbnail}/>
                    </Button>
                    {/* </Button> */}
                    <CCardBody
                      style={{backgroundImage:`url(${image})`,height:`${imageHeight}`,backgroundRepeat:'no-repeat',backgroundPosition: 'center',marginTop:5}}>
                      <Fade in={imageLoading}><CSpinner grow color="primary"/></Fade>
                    </CCardBody>
                  </CCol>    
                  <CCol xs="12" md="6">
                    <Button size="sm" color="#2a1ab9" variant="contained" >
                      <VideoLibraryIcon /> &nbsp; Upload Video
                      <input type="file" accept="video/*" style={{display:"none"}} onChange={setVideoThumbnail}/>
                    </Button>
                    <CCardBody>                      
                      <ReactPlayer controls width="250" height="125" url={video} style={{height:`${videoHeight}`,backgroundRepeat:'no-repeat',backgroundPosition: 'center',marginTop:5}} />
                      <Fade in={videoLoading}><CSpinner grow color="primary"/></Fade>
                    </CCardBody>
                  </CCol>     
                </CFormGroup>
              </CForm>
            </CCardBody>
            <CCardFooter>
              <CButton type="submit" size="sm" color="primary" onClick={handleSubmit}><CIcon name="cil-save" /> Save</CButton>
            </CCardFooter>
          </CCard>
        </CCol>
        <CCol sm="12" lg="6">
          <Snackbar
            anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
            open={toast}
            autoHideDuration={3000}
            onClose={() => { showToast(false); getCategoryList(); }}>
            <Alert
              severity={toastClass}
              action={
                <CButton  onClick={(e) => { e.preventDefault(); showToast(false); getCategoryList();  }}><CIcon name="cil-x" /></CButton>
              }
            >
              {toastMessage}
            </Alert>
          </Snackbar>
        </CCol>
      </CRow>
    </div>
  )
}
